option.value:
package com.example.campus_lost_found

import android.os.Bundle
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.appcompat.widget.SearchView
import androidx.fragment.app.Fragment
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.example.campus_lost_found.adapter.ItemsAdapter
import com.example.campus_lost_found.model.FoundItem
import com.example.campus_lost_found.repository.ItemRepository
import com.google.android.material.dialog.MaterialAlertDialogBuilder
import com.google.firebase.auth.FirebaseAuth

class FoundItemsFragment : Fragment() {

    private lateinit var recyclerView: RecyclerView
    private lateinit var searchView: SearchView
    private val itemRepository = ItemRepository()
    private val currentUserId: String
        get() = FirebaseAuth.getInstance().currentUser?.uid ?: ""
    private val currentUserName: String
        get() = FirebaseAuth.getInstance().currentUser?.displayName ?: "Anonymous User"

    private var foundItems = listOf<FoundItem>()

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        return inflater.inflate(R.layout.fragment_items_list, container, false)
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)

        recyclerView = view.findViewById(R.id.itemsRecyclerView)
        searchView = view.findViewById(R.id.searchView)

        setupRecyclerView()
        setupSearch()
        loadFoundItems()
    }

    private fun setupRecyclerView() {
        recyclerView.layoutManager = LinearLayoutManager(requireContext())
        recyclerView.setHasFixedSize(true)
    }

    private fun setupSearch() {
        searchView.setOnQueryTextListener(object : SearchView.OnQueryTextListener {
            override fun onQueryTextSubmit(query: String?): Boolean {
                filterItems(query)
                return false
            }

            override fun onQueryTextChange(newText: String?): Boolean {
                filterItems(newText)
                return false
            }
        })
    }

    private fun filterItems(query: String?) {
        if (query.isNullOrBlank()) {
            updateRecyclerView(foundItems)
            return
        }

        val filteredList = foundItems.filter { item ->
            item.name.contains(query, ignoreCase = true) ||
            item.description.contains(query, ignoreCase = true) ||
            item.category.contains(query, ignoreCase = true) ||
            item.location.contains(query, ignoreCase = true) ||
            item.keptAt.contains(query, ignoreCase = true)
        }

        updateRecyclerView(filteredList)
    }

    private fun loadFoundItems() {
        itemRepository.getFoundItems().get()
            .addOnSuccessListener { snapshot ->
                val items = snapshot.toObjects(FoundItem::class.java)
                foundItems = items
                updateRecyclerView(items)
            }
            .addOnFailureListener { exception ->
                showErrorDialog("Failed to load found items: ${exception.message}")
            }
    }

    private fun updateRecyclerView(items: List<FoundItem>) {
        val adapter = ItemsAdapter(
            items = items,
            isLostItemsList = false,
            currentUserId = currentUserId,
            onItemClick = { item ->
                // Show item details
                showItemDetailsDialog(item as FoundItem)
            },
            onClaimButtonClick = { item ->
                // Show claim confirmation dialog
                showClaimConfirmationDialog(item as FoundItem)
            }
        )
        recyclerView.adapter = adapter
    }

    private fun showItemDetailsDialog(item: FoundItem) {
        val claimStatus = if (item.claimed) {
            "Claimed by: ${item.claimedByName}"
        } else {
            "Not claimed"
        }

        val message = """
            Name: ${item.name}
            Category: ${item.category}
            Location: ${item.location}
            Description: ${item.description}
            Date Found: ${item.dateFound.toDate()}
            Kept at: ${item.keptAt}
            Status: $claimStatus
            Reported by: ${item.reportedByName}
        """.trimIndent()

        MaterialAlertDialogBuilder(requireContext())
            .setTitle("Item Details")
            .setMessage(message)
            .setPositiveButton("Close", null)
            .show()
    }

    private fun showClaimConfirmationDialog(item: FoundItem) {
        val dialogView = LayoutInflater.from(requireContext())
            .inflate(R.layout.dialog_claim_item, null)

        MaterialAlertDialogBuilder(requireContext())
            .setTitle("Claim This Item")
            .setView(dialogView)
            .setMessage("Are you sure you want to claim this item? The finder will be notified of your claim.")
            .setPositiveButton("Claim") { _, _ ->
                processClaimRequest(item)
            }
            .setNegativeButton("Cancel", null)
            .show()
    }

    private fun processClaimRequest(item: FoundItem) {
        if (currentUserId.isEmpty()) {
            showErrorDialog("You must be logged in to claim items.")
            return
        }

        itemRepository.claimItem(item.id, currentUserId, currentUserName)
            .addOnSuccessListener {
                showSuccessDialog("Claim request sent successfully. The finder will be notified.")
                loadFoundItems() // Refresh the list
            }
            .addOnFailureListener { exception ->
                showErrorDialog("Failed to claim item: ${exception.message}")
            }
    }

    private fun showSuccessDialog(message: String) {
        MaterialAlertDialogBuilder(requireContext())
            .setTitle("Success")
            .setMessage(message)
            .setPositiveButton("OK", null)
            .show()
    }

    private fun showErrorDialog(message: String) {
        MaterialAlertDialogBuilder(requireContext())
            .setTitle("Error")
            .setMessage(message)
            .setPositiveButton("OK", null)
            .show()
    }

    override fun onResume() {
        super.onResume()
        loadFoundItems() // Refresh data when coming back to this fragment
    }
}

package com.example.campus_lost_found

import android.os.Bundle
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.appcompat.widget.SearchView
import androidx.fragment.app.Fragment
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.example.campus_lost_found.adapter.ItemsAdapter
import com.example.campus_lost_found.model.FoundItem
import com.example.campus_lost_found.repository.ItemRepository
import com.google.android.material.dialog.MaterialAlertDialogBuilder
import com.google.firebase.auth.FirebaseAuth

class FoundItemsFragment : Fragment() {

    private lateinit var recyclerView: RecyclerView
    private lateinit var searchView: SearchView
    private val itemRepository = ItemRepository()
    private val currentUserId: String
        get() = FirebaseAuth.getInstance().currentUser?.uid ?: ""
    private val currentUserName: String
        get() = FirebaseAuth.getInstance().currentUser?.displayName ?: "Anonymous User"

    private var foundItems = listOf<FoundItem>()

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        return inflater.inflate(R.layout.fragment_items_list, container, false)
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)

        recyclerView = view.findViewById(R.id.itemsRecyclerView)
        searchView = view.findViewById(R.id.searchView)

        setupRecyclerView()
        setupSearch()
        loadFoundItems()
    }

    private fun setupRecyclerView() {
        recyclerView.layoutManager = LinearLayoutManager(requireContext())
        recyclerView.setHasFixedSize(true)
    }

    private fun setupSearch() {
        searchView.setOnQueryTextListener(object : SearchView.OnQueryTextListener {
            override fun onQueryTextSubmit(query: String?): Boolean {
                filterItems(query)
                return false
            }

            override fun onQueryTextChange(newText: String?): Boolean {
                filterItems(newText)
                return false
            }
        })
    }

    private fun filterItems(query: String?) {
        if (query.isNullOrBlank()) {
            updateRecyclerView(foundItems)
            return
        }

        val filteredList = foundItems.filter { item ->
            item.name.contains(query, ignoreCase = true) ||
            item.description.contains(query, ignoreCase = true) ||
            item.category.contains(query, ignoreCase = true) ||
            item.location.contains(query, ignoreCase = true) ||
            item.keptAt.contains(query, ignoreCase = true)
        }

        updateRecyclerView(filteredList)
    }

    private fun loadFoundItems() {
        itemRepository.getFoundItems().get()
            .addOnSuccessListener { snapshot ->
                val items = snapshot.toObjects(FoundItem::class.java)
                foundItems = items
                updateRecyclerView(items)
            }
            .addOnFailureListener { exception ->
                showErrorDialog("Failed to load found items: ${exception.message}")
            }
    }

    private fun updateRecyclerView(items: List<FoundItem>) {
        val adapter = ItemsAdapter(
            items = items.toMutableList(),
            isLostItemsList = false,
            currentUserId = currentUserId,
            onItemClick = { item ->
                // Show item details
                showItemDetailsDialog(item as FoundItem)
            },
            onClaimButtonClick = { item ->
                // Show claim confirmation dialog
                showClaimConfirmationDialog(item as FoundItem)
            }
        )
        recyclerView.adapter = adapter
    }

    private fun showItemDetailsDialog(item: FoundItem) {
        val claimStatus = if (item.claimed) {
            "Claimed by: ${item.claimedByName}"
        } else {
            "Not claimed"
        }

        val message = """
            Name: ${item.name}
            Category: ${item.category}
            Location: ${item.location}
            Description: ${item.description}
            Date Found: ${item.dateFound.toDate()}
            Kept at: ${item.keptAt}
            Status: $claimStatus
            Reported by: ${item.reportedByName}
        """.trimIndent()

        MaterialAlertDialogBuilder(requireContext())
            .setTitle("Item Details")
            .setMessage(message)
            .setPositiveButton("Close", null)
            .show()
    }

    private fun showClaimConfirmationDialog(item: FoundItem) {
        val dialogView = LayoutInflater.from(requireContext())
            .inflate(R.layout.dialog_claim_item, null)

        MaterialAlertDialogBuilder(requireContext())
            .setTitle("Claim This Item")
            .setView(dialogView)
            .setMessage("Are you sure you want to claim this item? The finder will be notified of your claim.")
            .setPositiveButton("Claim") { _, _ ->
                processClaimRequest(item)
            }
            .setNegativeButton("Cancel", null)
            .show()
    }

    private fun processClaimRequest(item: FoundItem) {
        if (currentUserId.isEmpty()) {
            showErrorDialog("You must be logged in to claim items.")
            return
        }

        itemRepository.claimItem(item.id, currentUserId, currentUserName)
            .addOnSuccessListener {
                showSuccessDialog("Claim request sent successfully. The finder will be notified.")
                loadFoundItems() // Refresh the list
            }
            .addOnFailureListener { exception ->
                showErrorDialog("Failed to claim item: ${exception.message}")
            }
    }

    private fun showSuccessDialog(message: String) {
        MaterialAlertDialogBuilder(requireContext())
            .setTitle("Success")
            .setMessage(message)
            .setPositiveButton("OK", null)
            .show()
    }

    private fun showErrorDialog(message: String) {
        MaterialAlertDialogBuilder(requireContext())
            .setTitle("Error")
            .setMessage(message)
            .setPositiveButton("OK", null)
            .show()
    }

    override fun onResume() {
        super.onResume()
        loadFoundItems() // Refresh data when coming back to this fragment
    }
}

package com.example.campus_lost_found

import android.content.Intent
import android.os.Bundle
import android.util.Log
import android.view.View
import android.widget.Button
import android.widget.EditText
import android.widget.ProgressBar
import android.widget.TextView
import android.widget.Toast
import androidx.appcompat.app.AppCompatActivity
import com.google.android.gms.auth.api.signin.GoogleSignIn
import com.google.android.gms.auth.api.signin.GoogleSignInClient
import com.google.android.gms.auth.api.signin.GoogleSignInOptions
import com.google.android.gms.common.SignInButton
import com.google.android.gms.common.api.ApiException
import com.google.android.material.dialog.MaterialAlertDialogBuilder
import com.google.android.material.textfield.TextInputLayout
import com.google.firebase.auth.FirebaseAuth
import com.google.firebase.auth.FirebaseAuthInvalidCredentialsException
import com.google.firebase.auth.FirebaseAuthInvalidUserException
import com.google.firebase.auth.GoogleAuthProvider

class LoginActivity : AppCompatActivity() {

    private lateinit var auth: FirebaseAuth
    private lateinit var googleSignInClient: GoogleSignInClient
    private val RC_SIGN_IN = 9001
    private val TAG = "LoginActivity"

    // UI elements
    private lateinit var emailLayout: TextInputLayout
    private lateinit var passwordLayout: TextInputLayout
    private lateinit var emailInput: EditText
    private lateinit var passwordInput: EditText
    private lateinit var loginButton: Button
    private lateinit var signupButton: Button
    private lateinit var forgotPasswordText: TextView
    private lateinit var useDefaultEmailText: TextView
    private lateinit var progressBar: ProgressBar
    private lateinit var googleSignInButton: SignInButton
    private lateinit var skipButton: Button

    // Default credentials
    private val defaultEmail = "sgp.noreplydce@gmail.com"
    private val defaultPassword = "campus123"

    // Modern Activity Result API for Google Sign-In
        super.onCreate(savedInstanceState)

        // Force apply the dark theme
        setTheme(R.style.Theme_CAMPUS_LOST_FOUND)

        setContentView(R.layout.activity_login)

        try {
            // Initialize Firebase Auth
            auth = FirebaseAuth.getInstance()

            // Configure Google Sign In with direct client ID instead of using resource
            val gso = GoogleSignInOptions.Builder(GoogleSignInOptions.DEFAULT_SIGN_IN)
                .requestIdToken("810761260274-j2nkf7a20q4vu2rdbbd3b0hs51cgc3te.apps.googleusercontent.com")
                .requestEmail()
                .build()

            googleSignInClient = GoogleSignIn.getClient(this, gso)

            // Check if user is already signed in
            if (auth.currentUser != null) {
                startMainActivity()
                return
            }

            // Initialize views
            initializeViews()

            // Set up listeners
            setupListeners()

        } catch (e: Exception) {
            Log.e(TAG, "Critical error during initialization: ${e.message}")
            Toast.makeText(this, "Error initializing app. Proceeding without authentication.",
                Toast.LENGTH_LONG).show()
            startMainActivity()
        }
    }

    private fun initializeViews() {
        emailLayout = findViewById(R.id.emailLayout)
        passwordLayout = findViewById(R.id.passwordLayout)
        emailInput = findViewById(R.id.emailInput)
        passwordInput = findViewById(R.id.passwordInput)
        loginButton = findViewById(R.id.loginButton)
        signupButton = findViewById(R.id.signupButton)
        forgotPasswordText = findViewById(R.id.forgotPasswordText)
        useDefaultEmailText = findViewById(R.id.useDefaultEmailText)
        progressBar = findViewById(R.id.progressBar)
        googleSignInButton = findViewById(R.id.googleSignInButton)
        skipButton = findViewById(R.id.skipButton)
    }

    private fun setupListeners() {
        // Skip authentication button
        skipButton.setOnClickListener {
            // Skip authentication and continue to main activity
            Toast.makeText(this,
                "Proceeding without authentication",
                Toast.LENGTH_SHORT).show()
            startMainActivity()
        }

        // Google Sign-In button
        googleSignInButton.setOnClickListener {
            signInWithGoogle()
        }

        // Default email login
        useDefaultEmailText.setOnClickListener {
            // First try to sign in with default credentials
            signInWithEmailPassword(defaultEmail, defaultPassword)
        }

        // Regular login button
        loginButton.setOnClickListener {
            val email = emailInput.text.toString().trim()
            val password = passwordInput.text.toString().trim()

            if (validateInput(email, password)) {
                signInWithEmailPassword(email, password)
            }
        }

        // Sign up button
        signupButton.setOnClickListener {
            val email = emailInput.text.toString().trim()
            val password = passwordInput.text.toString().trim()

            if (validateInput(email, password)) {
                createAccount(email, password)
            }
        }

        // Forgot password text
        forgotPasswordText.setOnClickListener {
            showForgotPasswordDialog()
        }
    }

    private fun validateInput(email: String, password: String): Boolean {
        var isValid = true

        if (email.isEmpty()) {
            emailLayout.error = "Email is required"
            isValid = false
        } else {
            emailLayout.error = null
        }

        if (password.isEmpty()) {
            passwordLayout.error = "Password is required"
            isValid = false
        } else if (password.length < 6) {
            passwordLayout.error = "Password must be at least 6 characters"
            isValid = false
        } else {
            passwordLayout.error = null
        }

        return isValid
    }

    private fun signInWithGoogle() {
        try {
            showProgress(true)
            val signInIntent = googleSignInClient.signInIntent
            startActivityForResult(signInIntent, RC_SIGN_IN)
        } catch (e: Exception) {
            showProgress(false)
            Log.e(TAG, "Error starting Google Sign-In: ${e.message}")
            Toast.makeText(
                this,
                "Google Sign-In is not available. Proceeding without authentication.",
                Toast.LENGTH_LONG
            ).show()
            // Just proceed to main activity instead of showing dialog
            startMainActivity()
        }
    }

    private fun signInWithEmailPassword(email: String, password: String) {
        showProgress(true)

        auth.signInWithEmailAndPassword(email, password)
            .addOnCompleteListener(this) { task ->
                showProgress(false)
                if (task.isSuccessful) {
                    Log.d(TAG, "signInWithEmail:success")
                    startMainActivity()
                } else {
                    Log.w(TAG, "signInWithEmail:failure", task.exception)

                    // Provide more specific error messages
                    when (task.exception) {
                        is FirebaseAuthInvalidUserException -> {
                            // If using the default email and it fails, create the account
                            if (email == defaultEmail) {
                                createAccount(defaultEmail, defaultPassword)
                            } else {
                                Toast.makeText(this, "User does not exist. Please create an account first.",
                                    Toast.LENGTH_SHORT).show()
                            }
                        }
                        is FirebaseAuthInvalidCredentialsException -> {
                            Toast.makeText(this, "Invalid password. Please try again.",
                                Toast.LENGTH_SHORT).show()
                        }
                        else -> {
                            Toast.makeText(this, "Authentication failed: ${task.exception?.message}",
                                Toast.LENGTH_SHORT).show()

                            // Show dialog offering alternatives
                            showAuthenticationFailedDialog()
                        }
                    }
                }
            }
    }

    private fun createAccount(email: String, password: String) {
        showProgress(true)

        auth.createUserWithEmailAndPassword(email, password)
            .addOnCompleteListener(this) { task ->
                showProgress(false)
                if (task.isSuccessful) {
                    Log.d(TAG, "createUserWithEmail:success")
                    Toast.makeText(this, "Account created successfully!", Toast.LENGTH_SHORT).show()
                    startMainActivity()
                } else {
                    Log.w(TAG, "createUserWithEmail:failure", task.exception)
                    Toast.makeText(this, "Registration failed: ${task.exception?.message}",
                        Toast.LENGTH_SHORT).show()

                    // If everything fails, offer to proceed without authentication
                    showAuthenticationFailedDialog()
                }
            }
    }

    private fun showForgotPasswordDialog() {
        val emailInput = EditText(this)
        emailInput.hint = "Enter your email"

        MaterialAlertDialogBuilder(this)
            .setTitle("Reset Password")
            .setMessage("Enter your email to receive a password reset link")
            .setView(emailInput)
            .setPositiveButton("Send") { _, _ ->
                val email = emailInput.text.toString().trim()
                if (email.isNotEmpty()) {
                    resetPassword(email)
                } else {
                    Toast.makeText(this, "Email cannot be empty", Toast.LENGTH_SHORT).show()
                }
            }
            .setNegativeButton("Cancel", null)
            .show()
    }

    private fun resetPassword(email: String) {
        showProgress(true)

        auth.sendPasswordResetEmail(email)
            .addOnCompleteListener { task ->
                showProgress(false)
                if (task.isSuccessful) {
                    Toast.makeText(this, "Reset link sent to your email", Toast.LENGTH_SHORT).show()
                } else {
                    Toast.makeText(this, "Failed to send reset email: ${task.exception?.message}",
                        Toast.LENGTH_SHORT).show()
                }
            }
    }

    private fun showAuthenticationFailedDialog() {
        MaterialAlertDialogBuilder(this)
            .setTitle("Authentication Failed")
            .setMessage("Would you like to continue without signing in? Some features may be limited.")
            .setPositiveButton("Continue Without Sign In") { _, _ ->
                startMainActivity()
            }
            .setNegativeButton("Try Again", null)
            .show()
    }

    private fun showGoogleSignInNotAvailableDialog() {
        MaterialAlertDialogBuilder(this)
            .setTitle("Google Sign-In Not Available")
            .setMessage("Please use email/password authentication or proceed without authentication.")
            .setPositiveButton("Skip Authentication") { _, _ ->
                startMainActivity()
            }
            .setNegativeButton("Try Email/Password", null)
            .show()
    }

    private fun handleSignInResult(data: Intent?) {
        try {
            val task = GoogleSignIn.getSignedInAccountFromIntent(data)
            val account = task.getResult(ApiException::class.java)
            if (account != null && account.idToken != null) {
                firebaseAuthWithGoogle(account.idToken!!)
            } else {
                throw Exception("Google account or token is null")
            }
    override fun onActivityResult(requestCode: Int, resultCode: Int, data: Intent?) {
        super.onActivityResult(requestCode, resultCode, data)

        if (requestCode == RC_SIGN_IN) {
            try {
                val task = GoogleSignIn.getSignedInAccountFromIntent(data)
                val account = task.getResult(ApiException::class.java)
                if (account != null && account.idToken != null) {
                    firebaseAuthWithGoogle(account.idToken!!)
                } else {
                    throw Exception("Google account or token is null")
                }
            } catch (e: ApiException) {
                showProgress(false)
                Log.w(TAG, "Google sign in failed: ${e.statusCode}", e)
                Toast.makeText(this, "Google Sign-In failed. Proceeding without authentication.",
                    Toast.LENGTH_SHORT).show()
                startMainActivity()
            } catch (e: Exception) {
                showProgress(false)
                Log.e(TAG, "Unexpected error in Google Sign-In: ${e.message}")
                startMainActivity()
            }
                    Log.d(TAG, "signInWithCredential:success")
                    startMainActivity()
                } else {
                    Log.w(TAG, "signInWithCredential:failure", task.exception)
                    Toast.makeText(this, "Google authentication failed: ${task.exception?.message}",
                        Toast.LENGTH_SHORT).show()

                    // If Google auth fails, show dialog offering alternatives
                    showAuthenticationFailedDialog()
                }
            }
    }

    private fun showProgress(show: Boolean) {
        progressBar.visibility = if (show) View.VISIBLE else View.GONE
    }

    private fun startMainActivity() {
        val intent = Intent(this, MainActivity::class.java)
        startActivity(intent)
        finish()
    }
}

package com.example.campus_lost_found

import android.content.Intent
import android.os.Bundle
import android.util.Log
import android.view.View
import android.widget.Button
import android.widget.EditText
import android.widget.ProgressBar
import android.widget.TextView
import android.widget.Toast
import androidx.activity.result.contract.ActivityResultContracts
import androidx.appcompat.app.AppCompatActivity
import com.google.android.gms.auth.api.signin.GoogleSignIn
import com.google.android.gms.auth.api.signin.GoogleSignInClient
import com.google.android.gms.auth.api.signin.GoogleSignInOptions
import com.google.android.gms.common.SignInButton
import com.google.android.gms.common.api.ApiException
import com.google.android.material.dialog.MaterialAlertDialogBuilder
import com.google.android.material.textfield.TextInputLayout
import com.google.firebase.auth.FirebaseAuth
import com.google.firebase.auth.FirebaseAuthInvalidCredentialsException
import com.google.firebase.auth.FirebaseAuthInvalidUserException
import com.google.firebase.auth.GoogleAuthProvider

class LoginActivity : AppCompatActivity() {

    private lateinit var auth: FirebaseAuth
    private lateinit var googleSignInClient: GoogleSignInClient
    private val TAG = "LoginActivity"

    // UI elements
    private lateinit var emailLayout: TextInputLayout
    private lateinit var passwordLayout: TextInputLayout
    private lateinit var emailInput: EditText
    private lateinit var passwordInput: EditText
    private lateinit var loginButton: Button
    private lateinit var signupButton: Button
    private lateinit var forgotPasswordText: TextView
    private lateinit var useDefaultEmailText: TextView
    private lateinit var progressBar: ProgressBar
    private lateinit var googleSignInButton: SignInButton
    private lateinit var skipButton: Button

    // Default credentials
    private val defaultEmail = "sgp.noreplydce@gmail.com"
    private val defaultPassword = "campus123"

    // Modern Activity Result API for Google Sign-In
    private val googleSignInLauncher = registerForActivityResult(
        ActivityResultContracts.StartActivityForResult()
    ) { result ->
        if (result.resultCode == RESULT_OK) {
            handleSignInResult(result.data)
        }
    }

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)

        // Force apply the dark theme
        setTheme(R.style.Theme_CAMPUS_LOST_FOUND)

        setContentView(R.layout.activity_login)

        try {
            // Initialize Firebase Auth
            auth = FirebaseAuth.getInstance()

            // Configure Google Sign In using the correct client ID from google-services.json
            val gso = GoogleSignInOptions.Builder(GoogleSignInOptions.DEFAULT_SIGN_IN)
                .requestIdToken("810761260274-qpi92nq7i379d91ob1r2v35f7rpljc42.apps.googleusercontent.com")
                .requestEmail()
                .build()

            googleSignInClient = GoogleSignIn.getClient(this, gso)

            // Check if user is already signed in
            if (auth.currentUser != null) {
                startMainActivity()
                return
            }

            // Initialize views
            initializeViews()

            // Set up listeners
            setupListeners()

        } catch (e: Exception) {
            Log.e(TAG, "Critical error during initialization: ${e.message}")
            Toast.makeText(this, "Error initializing app. Proceeding without authentication.",
                Toast.LENGTH_LONG).show()
            startMainActivity()
        }
    }

    private fun initializeViews() {
        emailLayout = findViewById(R.id.emailLayout)
        passwordLayout = findViewById(R.id.passwordLayout)
        emailInput = findViewById(R.id.emailInput)
        passwordInput = findViewById(R.id.passwordInput)
        loginButton = findViewById(R.id.loginButton)
        signupButton = findViewById(R.id.signupButton)
        forgotPasswordText = findViewById(R.id.forgotPasswordText)
        useDefaultEmailText = findViewById(R.id.useDefaultEmailText)
        progressBar = findViewById(R.id.progressBar)
        googleSignInButton = findViewById(R.id.googleSignInButton)
        skipButton = findViewById(R.id.skipButton)
    }

    private fun setupListeners() {
        // Skip authentication button
        skipButton.setOnClickListener {
            // Skip authentication and continue to main activity
            Toast.makeText(this,
                "Proceeding without authentication",
                Toast.LENGTH_SHORT).show()
            startMainActivity()
        }

        // Google Sign-In button
        googleSignInButton.setOnClickListener {
            signInWithGoogle()
        }

        // Default email login
        useDefaultEmailText.setOnClickListener {
            // First try to sign in with default credentials
            signInWithEmailPassword(defaultEmail, defaultPassword)
        }

        // Regular login button
        loginButton.setOnClickListener {
            val email = emailInput.text.toString().trim()
            val password = passwordInput.text.toString().trim()

            if (validateInput(email, password)) {
                signInWithEmailPassword(email, password)
            }
        }

        // Sign up button
        signupButton.setOnClickListener {
            val email = emailInput.text.toString().trim()
            val password = passwordInput.text.toString().trim()

            if (validateInput(email, password)) {
                createAccount(email, password)
            }
        }

        // Forgot password text
        forgotPasswordText.setOnClickListener {
            showForgotPasswordDialog()
        }
    }

    private fun validateInput(email: String, password: String): Boolean {
        var isValid = true

        if (email.isEmpty()) {
            emailLayout.error = "Email is required"
            isValid = false
        } else {
            emailLayout.error = null
        }

        if (password.isEmpty()) {
            passwordLayout.error = "Password is required"
            isValid = false
        } else if (password.length < 6) {
            passwordLayout.error = "Password must be at least 6 characters"
            isValid = false
        } else {
            passwordLayout.error = null
        }

        return isValid
    }

    private fun signInWithGoogle() {
        try {
            showProgress(true)
            val signInIntent = googleSignInClient.signInIntent
            googleSignInLauncher.launch(signInIntent)
        } catch (e: Exception) {
            showProgress(false)
            Log.e(TAG, "Error starting Google Sign-In: ${e.message}")
            Toast.makeText(
                this,
                "Google Sign-In is not available. Proceeding without authentication.",
                Toast.LENGTH_LONG
            ).show()
            // Just proceed to main activity instead of showing dialog
            startMainActivity()
        }
    }

    private fun signInWithEmailPassword(email: String, password: String) {
        showProgress(true)

        auth.signInWithEmailAndPassword(email, password)
            .addOnCompleteListener(this) { task ->
                showProgress(false)
                if (task.isSuccessful) {
                    Log.d(TAG, "signInWithEmail:success")
                    startMainActivity()
                } else {
                    Log.w(TAG, "signInWithEmail:failure", task.exception)

                    // Provide more specific error messages
                    when (task.exception) {
                        is FirebaseAuthInvalidUserException -> {
                            // If using the default email and it fails, create the account
                            if (email == defaultEmail) {
                                createAccount(defaultEmail, defaultPassword)
                            } else {
                                Toast.makeText(this, "User does not exist. Please create an account first.",
                                    Toast.LENGTH_SHORT).show()
                            }
                        }
                        is FirebaseAuthInvalidCredentialsException -> {
                            Toast.makeText(this, "Invalid password. Please try again.",
                                Toast.LENGTH_SHORT).show()
                        }
                        else -> {
                            Toast.makeText(this, "Authentication failed: ${task.exception?.message}",
                                Toast.LENGTH_SHORT).show()

                            // Show dialog offering alternatives
                            showAuthenticationFailedDialog()
                        }
                    }
                }
            }
    }

    private fun createAccount(email: String, password: String) {
        showProgress(true)

        auth.createUserWithEmailAndPassword(email, password)
            .addOnCompleteListener(this) { task ->
                showProgress(false)
                if (task.isSuccessful) {
                    Log.d(TAG, "createUserWithEmail:success")
                    Toast.makeText(this, "Account created successfully!", Toast.LENGTH_SHORT).show()
                    startMainActivity()
                } else {
                    Log.w(TAG, "createUserWithEmail:failure", task.exception)
                    Toast.makeText(this, "Registration failed: ${task.exception?.message}",
                        Toast.LENGTH_SHORT).show()

                    // If everything fails, offer to proceed without authentication
                    showAuthenticationFailedDialog()
                }
            }
    }

    private fun showForgotPasswordDialog() {
        val emailInput = EditText(this)
        emailInput.hint = "Enter your email"

        MaterialAlertDialogBuilder(this)
            .setTitle("Reset Password")
            .setMessage("Enter your email to receive a password reset link")
            .setView(emailInput)
            .setPositiveButton("Send") { _, _ ->
                val email = emailInput.text.toString().trim()
                if (email.isNotEmpty()) {
                    resetPassword(email)
                } else {
                    Toast.makeText(this, "Email cannot be empty", Toast.LENGTH_SHORT).show()
                }
            }
            .setNegativeButton("Cancel", null)
            .show()
    }

    private fun resetPassword(email: String) {
        showProgress(true)

        auth.sendPasswordResetEmail(email)
            .addOnCompleteListener { task ->
                showProgress(false)
                if (task.isSuccessful) {
                    Toast.makeText(this, "Reset link sent to your email", Toast.LENGTH_SHORT).show()
                } else {
                    Toast.makeText(this, "Failed to send reset email: ${task.exception?.message}",
                        Toast.LENGTH_SHORT).show()
                }
            }
    }

    private fun showAuthenticationFailedDialog() {
        MaterialAlertDialogBuilder(this)
            .setTitle("Authentication Failed")
            .setMessage("Would you like to continue without signing in? Some features may be limited.")
            .setPositiveButton("Continue Without Sign In") { _, _ ->
                startMainActivity()
            }
            .setNegativeButton("Try Again", null)
            .show()
    }

    private fun showGoogleSignInNotAvailableDialog() {
        MaterialAlertDialogBuilder(this)
            .setTitle("Google Sign-In Not Available")
            .setMessage("Please use email/password authentication or proceed without authentication.")
            .setPositiveButton("Skip Authentication") { _, _ ->
                startMainActivity()
            }
            .setNegativeButton("Try Email/Password", null)
            .show()
    }

    private fun handleSignInResult(data: Intent?) {
        try {
            val task = GoogleSignIn.getSignedInAccountFromIntent(data)
            val account = task.getResult(ApiException::class.java)
            if (account != null && account.idToken != null) {
                firebaseAuthWithGoogle(account.idToken!!)
            } else {
                throw Exception("Google account or token is null")
            }
        } catch (e: ApiException) {
            showProgress(false)
            Log.w(TAG, "Google sign in failed: ${e.statusCode}", e)
            Toast.makeText(this, "Google Sign-In failed. Proceeding without authentication.",
                Toast.LENGTH_SHORT).show()
            startMainActivity()
        } catch (e: Exception) {
            showProgress(false)
            Log.e(TAG, "Unexpected error in Google Sign-In: ${e.message}")
            startMainActivity()
        }
    }

    private fun firebaseAuthWithGoogle(idToken: String) {
        val credential = GoogleAuthProvider.getCredential(idToken, null)
        auth.signInWithCredential(credential)
            .addOnCompleteListener(this) { task ->
                showProgress(false)
                if (task.isSuccessful) {
                    Log.d(TAG, "signInWithCredential:success")
                    startMainActivity()
                } else {
                    Log.w(TAG, "signInWithCredential:failure", task.exception)
                    Toast.makeText(this, "Google authentication failed: ${task.exception?.message}",
                        Toast.LENGTH_SHORT).show()

                    // If Google auth fails, show dialog offering alternatives
                    showAuthenticationFailedDialog()
                }
            }
    }

    private fun showProgress(show: Boolean) {
        progressBar.visibility = if (show) View.VISIBLE else View.GONE
    }

    private fun startMainActivity() {
        val intent = Intent(this, MainActivity::class.java)
        startActivity(intent)
        finish()
    }
}

package com.example.campus_lost_found

import android.os.Bundle
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.appcompat.widget.SearchView
import androidx.fragment.app.Fragment
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.example.campus_lost_found.adapter.ItemsAdapter
import com.example.campus_lost_found.model.LostItem
import com.example.campus_lost_found.repository.ItemRepository
import com.google.android.material.dialog.MaterialAlertDialogBuilder
import com.google.firebase.auth.FirebaseAuth

class LostItemsFragment : Fragment() {

    private lateinit var recyclerView: RecyclerView
    private lateinit var searchView: SearchView
    private val itemRepository = ItemRepository()
    private val currentUserId: String
        get() = FirebaseAuth.getInstance().currentUser?.uid ?: ""

    private var lostItems = listOf<LostItem>()

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        return inflater.inflate(R.layout.fragment_items_list, container, false)
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)

        recyclerView = view.findViewById(R.id.itemsRecyclerView)
        searchView = view.findViewById(R.id.searchView)

        setupRecyclerView()
        setupSearch()
        loadLostItems()
    }

    private fun setupRecyclerView() {
        recyclerView.layoutManager = LinearLayoutManager(requireContext())
        recyclerView.setHasFixedSize(true)
    }

    private fun setupSearch() {
        searchView.setOnQueryTextListener(object : SearchView.OnQueryTextListener {
            override fun onQueryTextSubmit(query: String?): Boolean {
                filterItems(query)
                return false
            }

            override fun onQueryTextChange(newText: String?): Boolean {
                filterItems(newText)
                return false
            }
        })
    }

    private fun filterItems(query: String?) {
        if (query.isNullOrBlank()) {
            updateRecyclerView(lostItems)
            return
        }

        val filteredList = lostItems.filter { item ->
            item.name.contains(query, ignoreCase = true) ||
            item.description.contains(query, ignoreCase = true) ||
            item.category.contains(query, ignoreCase = true) ||
            item.location.contains(query, ignoreCase = true)
        }

        updateRecyclerView(filteredList)
    }

    private fun loadLostItems() {
        itemRepository.getLostItems().get()
            .addOnSuccessListener { snapshot ->
                val items = snapshot.toObjects(LostItem::class.java)
                lostItems = items
                updateRecyclerView(items)
            }
            .addOnFailureListener { exception ->
                showErrorDialog("Failed to load lost items: ${exception.message}")
            }
    }

    private fun updateRecyclerView(items: List<LostItem>) {
        val adapter = ItemsAdapter(
            items = items,
            isLostItemsList = true,
            currentUserId = currentUserId,
            onItemClick = { item ->
                showItemDetailsDialog(item as LostItem)
            },
            onClaimButtonClick = { item ->
                handleClaimRequest(item as LostItem)
            }
        )
        recyclerView.adapter = adapter
    }

    private fun showItemDetailsDialog(item: LostItem) {
        val message = """
            Name: ${item.name}
            Category: ${item.category}
            Location: ${item.location}
            Description: ${item.description}
            Date Lost: ${item.dateLost.toDate()}
            Reported by: ${item.reportedByName}
        """.trimIndent()

        MaterialAlertDialogBuilder(requireContext())
            .setTitle("Item Details")
            .setMessage(message)
            .setPositiveButton("Close", null)
            .show()
    }

    private fun handleClaimRequest(item: LostItem) {
        if (currentUserId.isEmpty()) {
            MaterialAlertDialogBuilder(requireContext())
                .setTitle("Authentication Required")
                .setMessage("Please sign in to contact the reporter.")
                .setPositiveButton("OK", null)
                .show()
            return
        }

        MaterialAlertDialogBuilder(requireContext())
            .setTitle("Contact Reporter")
            .setMessage("Do you think this item belongs to you? Contact the person who reported it lost.")
            .setPositiveButton("Contact") { _, _ ->
                showContactInfoDialog(item)
            }
            .setNegativeButton("Cancel", null)
            .show()
    }

    private fun showContactInfoDialog(item: LostItem) {
        MaterialAlertDialogBuilder(requireContext())
            .setTitle("Contact Information")
            .setMessage("Reporter: ${item.reportedByName}\n\nNote: In a full implementation, this would show contact details or open an in-app messaging system.")
            .setPositiveButton("OK", null)
            .show()
    }

    private fun showErrorDialog(message: String) {
        MaterialAlertDialogBuilder(requireContext())
            .setTitle("Error")
            .setMessage(message)
            .setPositiveButton("OK", null)
            .show()
    }

    override fun onResume() {
        super.onResume()
        loadLostItems() // Refresh data when coming back to this fragment
    }
}

package com.example.campus_lost_found

import android.os.Bundle
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.appcompat.widget.SearchView
import androidx.fragment.app.Fragment
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.example.campus_lost_found.adapter.ItemsAdapter
import com.example.campus_lost_found.model.LostItem
import com.example.campus_lost_found.repository.ItemRepository
import com.google.android.material.dialog.MaterialAlertDialogBuilder
import com.google.firebase.auth.FirebaseAuth

class LostItemsFragment : Fragment() {

    private lateinit var recyclerView: RecyclerView
    private lateinit var searchView: SearchView
    private val itemRepository = ItemRepository()
    private val currentUserId: String
        get() = FirebaseAuth.getInstance().currentUser?.uid ?: ""

    private var lostItems = listOf<LostItem>()

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        return inflater.inflate(R.layout.fragment_items_list, container, false)
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)

        recyclerView = view.findViewById(R.id.itemsRecyclerView)
        searchView = view.findViewById(R.id.searchView)

        setupRecyclerView()
        setupSearch()
        loadLostItems()
    }

    private fun setupRecyclerView() {
        recyclerView.layoutManager = LinearLayoutManager(requireContext())
        recyclerView.setHasFixedSize(true)
    }

    private fun setupSearch() {
        searchView.setOnQueryTextListener(object : SearchView.OnQueryTextListener {
            override fun onQueryTextSubmit(query: String?): Boolean {
                filterItems(query)
                return false
            }

            override fun onQueryTextChange(newText: String?): Boolean {
                filterItems(newText)
                return false
            }
        })
    }

    private fun filterItems(query: String?) {
        if (query.isNullOrBlank()) {
            updateRecyclerView(lostItems)
            return
        }

        val filteredList = lostItems.filter { item ->
            item.name.contains(query, ignoreCase = true) ||
            item.description.contains(query, ignoreCase = true) ||
            item.category.contains(query, ignoreCase = true) ||
            item.location.contains(query, ignoreCase = true)
        }

        updateRecyclerView(filteredList)
    }

    private fun loadLostItems() {
        itemRepository.getLostItems().get()
            .addOnSuccessListener { snapshot ->
                val items = snapshot.toObjects(LostItem::class.java)
                lostItems = items
                updateRecyclerView(items)
            }
            .addOnFailureListener { exception ->
                showErrorDialog("Failed to load lost items: ${exception.message}")
            }
    }

    private fun updateRecyclerView(items: List<LostItem>) {
        val adapter = ItemsAdapter(
            items = items.toMutableList(),
            isLostItemsList = true,
            currentUserId = currentUserId,
            onItemClick = { item ->
                showItemDetailsDialog(item as LostItem)
            },
            onClaimButtonClick = { item ->
                handleClaimRequest(item as LostItem)
            }
        )
        recyclerView.adapter = adapter
    }

    private fun showItemDetailsDialog(item: LostItem) {
        val message = """
            Name: ${item.name}
            Category: ${item.category}
            Location: ${item.location}
            Description: ${item.description}
            Date Lost: ${item.dateLost.toDate()}
            Reported by: ${item.reportedByName}
        """.trimIndent()

        MaterialAlertDialogBuilder(requireContext())
            .setTitle("Item Details")
            .setMessage(message)
            .setPositiveButton("Close", null)
            .show()
    }

    private fun handleClaimRequest(item: LostItem) {
        if (currentUserId.isEmpty()) {
            MaterialAlertDialogBuilder(requireContext())
                .setTitle("Authentication Required")
                .setMessage("Please sign in to contact the reporter.")
                .setPositiveButton("OK", null)
                .show()
            return
        }

        MaterialAlertDialogBuilder(requireContext())
            .setTitle("Contact Reporter")
            .setMessage("Do you think this item belongs to you? Contact the person who reported it lost.")
            .setPositiveButton("Contact") { _, _ ->
                showContactInfoDialog(item)
            }
            .setNegativeButton("Cancel", null)
            .show()
    }

    private fun showContactInfoDialog(item: LostItem) {
        MaterialAlertDialogBuilder(requireContext())
            .setTitle("Contact Information")
            .setMessage("Reporter: ${item.reportedByName}\n\nNote: In a full implementation, this would show contact details or open an in-app messaging system.")
            .setPositiveButton("OK", null)
            .show()
    }

    private fun showErrorDialog(message: String) {
        MaterialAlertDialogBuilder(requireContext())
            .setTitle("Error")
            .setMessage(message)
            .setPositiveButton("OK", null)
            .show()
    }

    override fun onResume() {
        super.onResume()
        loadLostItems() // Refresh data when coming back to this fragment
    }
}

package com.example.campus_lost_found

import android.content.Intent
import android.os.Bundle
import android.view.Menu
import android.view.MenuItem
import androidx.appcompat.app.AppCompatActivity
import androidx.appcompat.widget.Toolbar
import androidx.navigation.NavController
import androidx.navigation.fragment.NavHostFragment
import com.google.android.material.button.MaterialButton
import com.google.android.material.dialog.MaterialAlertDialogBuilder
import com.google.firebase.auth.FirebaseAuth

class MainActivity : AppCompatActivity() {

    private lateinit var logoutButton: MaterialButton
    private lateinit var navController: NavController

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)

        // Initialize views
        logoutButton = findViewById(R.id.logoutButton)

        // Set up toolbar
        val toolbar: Toolbar = findViewById(R.id.toolbar)
        setSupportActionBar(toolbar)
        supportActionBar?.title = getString(R.string.app_name)

        // Set up Navigation Component
        setupNavigation()
        setupListeners()

        // Check if user is logged in
        checkAuthState()
    }

    private fun setupNavigation() {
        val navHostFragment = supportFragmentManager
            .findFragmentById(R.id.nav_host_fragment) as NavHostFragment
        navController = navHostFragment.navController
    }

    private fun setupListeners() {
        logoutButton.setOnClickListener {
            showLogoutConfirmation()
        }
    }

    override fun onCreateOptionsMenu(menu: Menu): Boolean {
        menuInflater.inflate(R.menu.main_menu, menu)
        return true
    }

    override fun onOptionsItemSelected(item: MenuItem): Boolean {
        return when (item.itemId) {
            R.id.menu_logout -> {
                showLogoutConfirmation()
                true
            }
            R.id.menu_add_item -> {
                // Navigate to ReportItemActivity
                val intent = Intent(this, ReportItemActivity::class.java)
                startActivity(intent)
                true
            }
            else -> super.onOptionsItemSelected(item)
        }
    }

    private fun showLogoutConfirmation() {
        MaterialAlertDialogBuilder(this)
            .setTitle("Logout")
            .setMessage("Are you sure you want to logout?")
            .setPositiveButton("Logout") { _, _ ->
                // Logout
                FirebaseAuth.getInstance().signOut()
                // Return to login screen
                val intent = Intent(this, LoginActivity::class.java)
                intent.flags = Intent.FLAG_ACTIVITY_NEW_TASK or Intent.FLAG_ACTIVITY_CLEAR_TASK
                startActivity(intent)
                finish()
            }
            .setNegativeButton("Cancel", null)
            .show()
    }

    private fun checkAuthState() {
        if (FirebaseAuth.getInstance().currentUser == null) {
            // User not logged in, redirect to login
            val intent = Intent(this, LoginActivity::class.java)
            intent.flags = Intent.FLAG_ACTIVITY_NEW_TASK or Intent.FLAG_ACTIVITY_CLEAR_TASK
            startActivity(intent)
            finish()
        }
    }
}

