        return when (item.itemId) {
            R.id.menu_logout -> {
                showLogoutConfirmation()
                true
            }
            R.id.menu_refresh -> {
                // Refresh current fragment
                // You can implement refresh logic here if needed
                true
            }
            else -> super.onOptionsItemSelected(item)
        }
    }

    private fun showLogoutConfirmation() {
        MaterialAlertDialogBuilder(this)
            .setTitle("Logout")
            .setMessage("Are you sure you want to logout?")
            .setPositiveButton("Logout") { _, _ ->
                // Logout
                FirebaseAuth.getInstance().signOut()
                // Return to login screen
                val intent = Intent(this, LoginActivity::class.java)
                intent.flags = Intent.FLAG_ACTIVITY_NEW_TASK or Intent.FLAG_ACTIVITY_CLEAR_TASK
                startActivity(intent)
                finish()
            }
            .setNegativeButton("Cancel", null)
            .show()
    }

    private fun checkAuthState() {
        if (FirebaseAuth.getInstance().currentUser == null) {
            // User not logged in, redirect to login
            val intent = Intent(this, LoginActivity::class.java)
            intent.flags = Intent.FLAG_ACTIVITY_NEW_TASK or Intent.FLAG_ACTIVITY_CLEAR_TASK
            startActivity(intent)
            finish()
        }
    }
}

package com.example.campus_lost_found

import android.content.Intent
import android.os.Bundle
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.appcompat.widget.SearchView
import android.widget.TextView
import androidx.fragment.app.Fragment
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.example.campus_lost_found.adapter.ItemsAdapter
import com.example.campus_lost_found.model.FoundItem
import com.example.campus_lost_found.model.Item
import com.example.campus_lost_found.model.LostItem
import com.example.campus_lost_found.repository.ItemRepository
import com.google.android.material.dialog.MaterialAlertDialogBuilder
import com.google.android.material.tabs.TabLayout
import com.google.firebase.auth.FirebaseAuth

class MyReportsFragment : Fragment() {

    private lateinit var recyclerView: RecyclerView
    private lateinit var searchView: SearchView
    private lateinit var tabLayout: TabLayout
    private lateinit var noItemsTextView: TextView
    private val itemRepository = ItemRepository()
    private val currentUserId: String
        get() = FirebaseAuth.getInstance().currentUser?.uid ?: ""

    private var myLostItems = listOf<LostItem>()
    private var myFoundItems = listOf<FoundItem>()
    private var displayingLostItems = true

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        return inflater.inflate(R.layout.fragment_items_list, container, false)
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)

        recyclerView = view.findViewById(R.id.itemsRecyclerView)
        searchView = view.findViewById(R.id.searchView)
        tabLayout = view.findViewById(R.id.myReportsTabLayout)
        noItemsTextView = view.findViewById(R.id.empty_view)

        setupRecyclerView()
        setupTabLayout()
        setupSearch()
        loadMyItems()
    }

    private fun setupRecyclerView() {
        recyclerView.layoutManager = LinearLayoutManager(requireContext())
        recyclerView.setHasFixedSize(true)
    }

    private fun setupTabLayout() {
        tabLayout.addTab(tabLayout.newTab().setText("My Lost Items"))
        tabLayout.addTab(tabLayout.newTab().setText("My Found Items"))

        tabLayout.addOnTabSelectedListener(object : TabLayout.OnTabSelectedListener {
            override fun onTabSelected(tab: TabLayout.Tab?) {
                when (tab?.position) {
                    0 -> {
                        displayingLostItems = true
                        updateRecyclerView(myLostItems)
                    }
                    1 -> {
                        displayingLostItems = false
                        updateRecyclerView(myFoundItems)
                    }
                }
            }

            override fun onTabUnselected(tab: TabLayout.Tab?) {}
            override fun onTabReselected(tab: TabLayout.Tab?) {}
        })
    }

    private fun setupSearch() {
        searchView.setOnQueryTextListener(object : SearchView.OnQueryTextListener {
            override fun onQueryTextSubmit(query: String?): Boolean {
                filterItems(query)
                return false
            }

            override fun onQueryTextChange(newText: String?): Boolean {
                filterItems(newText)
                return false
            }
        })
    }

    private fun filterItems(query: String?) {
        if (query.isNullOrBlank()) {
            if (displayingLostItems) {
                updateRecyclerView(myLostItems)
            } else {
                updateRecyclerView(myFoundItems)
            }
            return
        }

        if (displayingLostItems) {
            val filteredList = myLostItems.filter { item ->
                item.name.contains(query, ignoreCase = true) ||
                item.description.contains(query, ignoreCase = true) ||
                item.category.contains(query, ignoreCase = true) ||
                item.location.contains(query, ignoreCase = true)
            }
            updateRecyclerView(filteredList)
        } else {
            val filteredList = myFoundItems.filter { item ->
                item.name.contains(query, ignoreCase = true) ||
                item.description.contains(query, ignoreCase = true) ||
                item.category.contains(query, ignoreCase = true) ||
                item.location.contains(query, ignoreCase = true)
            }
            updateRecyclerView(filteredList)
        }
    }

    private fun loadMyItems() {
        if (currentUserId.isEmpty()) {
            showLoginRequiredDialog()
            return
        }

        // Load lost items reported by current user
        itemRepository.getLostItemsByUser(currentUserId).get()
            .addOnSuccessListener { snapshot ->
                myLostItems = snapshot.toObjects(LostItem::class.java)
                if (displayingLostItems) {
                    updateRecyclerView(myLostItems)
                }
            }
            .addOnFailureListener { exception ->
                showErrorDialog("Failed to load your lost items: ${exception.message}")
            }

        // Load found items reported by current user
        itemRepository.getFoundItemsByUser(currentUserId).get()
            .addOnSuccessListener { snapshot ->
                myFoundItems = snapshot.toObjects(FoundItem::class.java)
                if (!displayingLostItems) {
                    updateRecyclerView(myFoundItems)
                }
            }
            .addOnFailureListener { exception ->
                showErrorDialog("Failed to load your found items: ${exception.message}")
            }
    }

    private fun <T : Item> updateRecyclerView(items: List<T>) {
        if (items.isEmpty()) {
            recyclerView.visibility = View.GONE
            noItemsTextView.visibility = View.VISIBLE
            noItemsTextView.text = if (displayingLostItems) {
                "You haven't reported any lost items yet"
            } else {
                "You haven't reported any found items yet"
            }
            return
        }

        recyclerView.visibility = View.VISIBLE
        noItemsTextView.visibility = View.GONE

        val adapter = ItemsAdapter(
            items = items,
            isLostItemsList = displayingLostItems,
            currentUserId = currentUserId,
            onItemClick = { item ->
                showItemOptionsDialog(item)
            }
        )
        recyclerView.adapter = adapter
    }

    private fun showItemOptionsDialog(item: Item) {
        MaterialAlertDialogBuilder(requireContext())
            .setTitle("Item Options")
            .setItems(arrayOf("View Details", "Edit", "Delete")) { _, which ->
                when (which) {
                    0 -> showItemDetailsDialog(item)
                    1 -> editItem(item)
                    2 -> showDeleteConfirmationDialog(item)
                }
            }
            .show()
    }

    private fun showItemDetailsDialog(item: Item) {
        val message = when (item) {
            is LostItem -> """
                Name: ${item.name}
                Category: ${item.category}
                Location: ${item.location}
                Description: ${item.description}
                Date Lost: ${item.dateLost.toDate()}
            """.trimIndent()

            is FoundItem -> {
                val claimStatus = if (item.claimed) {
                    "Claimed by: ${item.claimedByName}"
                } else {
                    "Not claimed"
                }

                """
                Name: ${item.name}
                Category: ${item.category}
                Location: ${item.location}
                Description: ${item.description}
                Date Found: ${item.dateFound.toDate()}
                Kept at: ${item.keptAt}
                Status: $claimStatus
                """.trimIndent()
            }

            else -> "Item details unavailable"
        }

        MaterialAlertDialogBuilder(requireContext())
            .setTitle("Item Details")
            .setMessage(message)
            .setPositiveButton("Close", null)
            .show()
    }

    private fun editItem(item: Item) {
        val intent = when (item) {
            is LostItem -> {
                ReportItemActivity.createEditIntent(
                    context = requireContext(),
                    isLostItem = true,
                    itemId = item.id
                )
            }
            is FoundItem -> {
                ReportItemActivity.createEditIntent(
                    context = requireContext(),
                    isLostItem = false,
                    itemId = item.id
                )
            }
            else -> null
        }

        intent?.let { startActivity(it) }
    }

    private fun showDeleteConfirmationDialog(item: Item) {
        MaterialAlertDialogBuilder(requireContext())
            .setTitle("Confirm Deletion")
            .setMessage("Are you sure you want to delete this item report? This action cannot be undone.")
            .setPositiveButton("Delete") { _, _ ->
                deleteItem(item)
            }
            .setNegativeButton("Cancel", null)
            .show()
    }

    private fun deleteItem(item: Item) {
        when (item) {
            is LostItem -> {
                itemRepository.deleteLostItem(item.id, currentUserId)
                    .addOnSuccessListener {
                        showSuccessDialog("Item deleted successfully")
                        loadMyItems()
                    }
                    .addOnFailureListener { exception ->
                        showErrorDialog("Failed to delete item: ${exception.message}")
                    }
            }
            is FoundItem -> {
                itemRepository.deleteFoundItem(item.id, currentUserId)
                    .addOnSuccessListener {
                        showSuccessDialog("Item deleted successfully")
                        loadMyItems()
                    }
                    .addOnFailureListener { exception ->
                        showErrorDialog("Failed to delete item: ${exception.message}")
                    }
            }
        }
    }

    private fun showLoginRequiredDialog() {
        MaterialAlertDialogBuilder(requireContext())
            .setTitle("Login Required")
            .setMessage("You must be logged in to view your reports.")
            .setPositiveButton("OK", null)
            .show()
    }

    private fun showSuccessDialog(message: String) {
        MaterialAlertDialogBuilder(requireContext())
            .setTitle("Success")
            .setMessage(message)
            .setPositiveButton("OK", null)
            .show()
    }

    private fun showErrorDialog(message: String) {
        MaterialAlertDialogBuilder(requireContext())
            .setTitle("Error")
            .setMessage(message)
            .setPositiveButton("OK", null)
            .show()
    }

    override fun onResume() {
        super.onResume()
        loadMyItems() // Refresh data when coming back to this fragment
    }
}

package com.example.campus_lost_found

import android.content.Intent
import android.os.Bundle
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.appcompat.widget.SearchView
import android.widget.TextView
import androidx.fragment.app.Fragment
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.example.campus_lost_found.adapter.ItemsAdapter
import com.example.campus_lost_found.model.FoundItem
import com.example.campus_lost_found.model.Item
import com.example.campus_lost_found.model.LostItem
import com.example.campus_lost_found.repository.ItemRepository
import com.google.android.material.dialog.MaterialAlertDialogBuilder
import com.google.android.material.tabs.TabLayout
import com.google.firebase.auth.FirebaseAuth

class MyReportsFragment : Fragment() {

    private lateinit var recyclerView: RecyclerView
    private lateinit var searchView: SearchView
    private lateinit var tabLayout: TabLayout
    private lateinit var noItemsTextView: TextView
    private val itemRepository = ItemRepository()
    private val currentUserId: String
        get() = FirebaseAuth.getInstance().currentUser?.uid ?: ""

    private var myLostItems = listOf<LostItem>()
    private var myFoundItems = listOf<FoundItem>()
    private var displayingLostItems = true

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        return inflater.inflate(R.layout.fragment_items_list, container, false)
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)

        recyclerView = view.findViewById(R.id.itemsRecyclerView)
        searchView = view.findViewById(R.id.searchView)
        tabLayout = view.findViewById(R.id.myReportsTabLayout)
        noItemsTextView = view.findViewById(R.id.empty_view)

        setupRecyclerView()
        setupTabLayout()
        setupSearch()
        loadMyItems()
    }

    private fun setupRecyclerView() {
        recyclerView.layoutManager = LinearLayoutManager(requireContext())
        recyclerView.setHasFixedSize(true)
    }

    private fun setupTabLayout() {
        tabLayout.addTab(tabLayout.newTab().setText("My Lost Items"))
        tabLayout.addTab(tabLayout.newTab().setText("My Found Items"))

        tabLayout.addOnTabSelectedListener(object : TabLayout.OnTabSelectedListener {
            override fun onTabSelected(tab: TabLayout.Tab?) {
                when (tab?.position) {
                    0 -> {
                        displayingLostItems = true
                        updateRecyclerView(myLostItems)
                    }
                    1 -> {
                        displayingLostItems = false
                        updateRecyclerView(myFoundItems)
                    }
                }
            }

            override fun onTabUnselected(tab: TabLayout.Tab?) {}
            override fun onTabReselected(tab: TabLayout.Tab?) {}
        })
    }

    private fun setupSearch() {
        searchView.setOnQueryTextListener(object : SearchView.OnQueryTextListener {
            override fun onQueryTextSubmit(query: String?): Boolean {
                filterItems(query)
                return false
            }

            override fun onQueryTextChange(newText: String?): Boolean {
                filterItems(newText)
                return false
            }
        })
    }

    private fun filterItems(query: String?) {
        if (query.isNullOrBlank()) {
            if (displayingLostItems) {
                updateRecyclerView(myLostItems)
            } else {
                updateRecyclerView(myFoundItems)
            }
            return
        }

        if (displayingLostItems) {
            val filteredList = myLostItems.filter { item ->
                item.name.contains(query, ignoreCase = true) ||
                item.description.contains(query, ignoreCase = true) ||
                item.category.contains(query, ignoreCase = true) ||
                item.location.contains(query, ignoreCase = true)
            }
            updateRecyclerView(filteredList)
        } else {
            val filteredList = myFoundItems.filter { item ->
                item.name.contains(query, ignoreCase = true) ||
                item.description.contains(query, ignoreCase = true) ||
                item.category.contains(query, ignoreCase = true) ||
                item.location.contains(query, ignoreCase = true)
            }
            updateRecyclerView(filteredList)
        }
    }

    private fun loadMyItems() {
        if (currentUserId.isEmpty()) {
            showLoginRequiredDialog()
            return
        }

        // Load lost items reported by current user
        itemRepository.getLostItemsByUser(currentUserId).get()
            .addOnSuccessListener { snapshot ->
                myLostItems = snapshot.toObjects(LostItem::class.java)
                if (displayingLostItems) {
                    updateRecyclerView(myLostItems)
                }
            }
            .addOnFailureListener { exception ->
                showErrorDialog("Failed to load your lost items: ${exception.message}")
            }

        // Load found items reported by current user
        itemRepository.getFoundItemsByUser(currentUserId).get()
            .addOnSuccessListener { snapshot ->
                myFoundItems = snapshot.toObjects(FoundItem::class.java)
                if (!displayingLostItems) {
                    updateRecyclerView(myFoundItems)
                }
            }
            .addOnFailureListener { exception ->
                showErrorDialog("Failed to load your found items: ${exception.message}")
            }
    }

    private fun <T : Item> updateRecyclerView(items: List<T>) {
        if (items.isEmpty()) {
            recyclerView.visibility = View.GONE
            noItemsTextView.visibility = View.VISIBLE
            noItemsTextView.text = if (displayingLostItems) {
                "You haven't reported any lost items yet"
            } else {
                "You haven't reported any found items yet"
            }
            return
        }

        recyclerView.visibility = View.VISIBLE
        noItemsTextView.visibility = View.GONE

        val adapter = ItemsAdapter(
            items = items.toMutableList(),
            isLostItemsList = displayingLostItems,
            currentUserId = currentUserId,
            onItemClick = { item ->
                showItemOptionsDialog(item)
            }
        )
        recyclerView.adapter = adapter
    }

    private fun showItemOptionsDialog(item: Item) {
        MaterialAlertDialogBuilder(requireContext())
            .setTitle("Item Options")
            .setItems(arrayOf("View Details", "Edit", "Delete")) { _, which ->
                when (which) {
                    0 -> showItemDetailsDialog(item)
                    1 -> editItem(item)
                    2 -> showDeleteConfirmationDialog(item)
                }
            }
            .show()
    }

    private fun showItemDetailsDialog(item: Item) {
        val message = when (item) {
            is LostItem -> """
                Name: ${item.name}
                Category: ${item.category}
                Location: ${item.location}
                Description: ${item.description}
                Date Lost: ${item.dateLost.toDate()}
            """.trimIndent()

            is FoundItem -> {
                val claimStatus = if (item.claimed) {
                    "Claimed by: ${item.claimedByName}"
                } else {
                    "Not claimed"
                }

                """
                Name: ${item.name}
                Category: ${item.category}
                Location: ${item.location}
                Description: ${item.description}
                Date Found: ${item.dateFound.toDate()}
                Kept at: ${item.keptAt}
                Status: $claimStatus
                """.trimIndent()
            }

            else -> "Item details unavailable"
        }

        MaterialAlertDialogBuilder(requireContext())
            .setTitle("Item Details")
            .setMessage(message)
            .setPositiveButton("Close", null)
            .show()
    }

    private fun editItem(item: Item) {
        val intent = when (item) {
            is LostItem -> {
                ReportItemActivity.createEditIntent(
                    context = requireContext(),
                    isLostItem = true,
                    itemId = item.id
                )
            }
            is FoundItem -> {
                ReportItemActivity.createEditIntent(
                    context = requireContext(),
                    isLostItem = false,
                    itemId = item.id
                )
            }
            else -> null
        }

        intent?.let { startActivity(it) }
    }

    private fun showDeleteConfirmationDialog(item: Item) {
        MaterialAlertDialogBuilder(requireContext())
            .setTitle("Confirm Deletion")
            .setMessage("Are you sure you want to delete this item report? This action cannot be undone.")
            .setPositiveButton("Delete") { _, _ ->
                deleteItem(item)
            }
            .setNegativeButton("Cancel", null)
            .show()
    }

    private fun deleteItem(item: Item) {
        when (item) {
            is LostItem -> {
                itemRepository.deleteLostItem(item.id, currentUserId)
                    .addOnSuccessListener {
                        showSuccessDialog("Item deleted successfully")
                        loadMyItems()
                    }
                    .addOnFailureListener { exception ->
                        showErrorDialog("Failed to delete item: ${exception.message}")
                    }
            }
            is FoundItem -> {
                itemRepository.deleteFoundItem(item.id, currentUserId)
                    .addOnSuccessListener {
                        showSuccessDialog("Item deleted successfully")
                        loadMyItems()
                    }
                    .addOnFailureListener { exception ->
                        showErrorDialog("Failed to delete item: ${exception.message}")
                    }
            }
        }
    }

    private fun showLoginRequiredDialog() {
        MaterialAlertDialogBuilder(requireContext())
            .setTitle("Login Required")
            .setMessage("You must be logged in to view your reports.")
            .setPositiveButton("OK", null)
            .show()
    }

    private fun showSuccessDialog(message: String) {
        MaterialAlertDialogBuilder(requireContext())
            .setTitle("Success")
            .setMessage(message)
            .setPositiveButton("OK", null)
            .show()
    }

    private fun showErrorDialog(message: String) {
        MaterialAlertDialogBuilder(requireContext())
            .setTitle("Error")
            .setMessage(message)
            .setPositiveButton("OK", null)
            .show()
    }

    override fun onResume() {
        super.onResume()
        loadMyItems() // Refresh data when coming back to this fragment
    }
}

package com.example.campus_lost_found

import android.app.Activity
import android.content.Context
import android.content.Intent
import android.net.Uri
import android.os.Bundle
import android.view.View
import android.widget.*
import androidx.activity.result.contract.ActivityResultContracts
import androidx.appcompat.app.AppCompatActivity
import com.bumptech.glide.Glide
import com.example.campus_lost_found.model.FoundItem
import com.example.campus_lost_found.model.LostItem
import com.example.campus_lost_found.repository.ItemRepository
import com.google.android.material.datepicker.MaterialDatePicker
import com.google.android.material.dialog.MaterialAlertDialogBuilder
import com.google.firebase.Timestamp
import com.google.firebase.auth.FirebaseAuth
import com.google.firebase.storage.FirebaseStorage
import com.google.firebase.storage.StorageReference
import com.google.firebase.storage.UploadTask
import java.text.SimpleDateFormat
import java.util.Date
import java.util.Locale
import java.util.UUID

class ReportItemActivity : AppCompatActivity() {

    private lateinit var titleTextView: TextView
    private lateinit var nameEditText: EditText
    private lateinit var descriptionEditText: EditText
    private lateinit var categorySpinner: Spinner
    private lateinit var locationEditText: EditText
    private lateinit var dateButton: Button
    private lateinit var keptAtLayout: View
    private lateinit var keptAtEditText: EditText
    private lateinit var itemImageView: ImageView
    private lateinit var uploadImageButton: Button
    private val storageRef = FirebaseStorage.getInstance().reference
    private lateinit var submitButton: Button

    private val storage = FirebaseStorage.getInstance()
    private val dateFormat = SimpleDateFormat("MMM dd, yyyy", Locale.getDefault())

    private var isLostItem = true
    private var selectedDate: Date = Date()
    private var imageUri: Uri? = null
    private var imageUrl: String = ""
    private var editItemId: String? = null
    private var editingExistingItem = false

    private val currentUserId: String
        get() = FirebaseAuth.getInstance().currentUser?.uid ?: ""
    private val currentUserName: String
        get() = FirebaseAuth.getInstance().currentUser?.displayName ?: "Anonymous User"

    private val getContent = registerForActivityResult(ActivityResultContracts.GetContent()) { uri ->
        uri?.let {
            imageUri = it
            Glide.with(this)
                .load(uri)
                .centerCrop()
                .into(itemImageView)
            itemImageView.visibility = View.VISIBLE
        }
    }

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_report_item)

        // Get intent data
        isLostItem = intent.getBooleanExtra("isLostItem", true)
        editItemId = intent.getStringExtra("itemId")
        editingExistingItem = editItemId != null

        // Initialize views
        initViews()
        setupUI()

        // Load item data if editing
        if (editingExistingItem) {
            loadItemData()
        }
    }

    private fun initViews() {
        titleTextView = findViewById(R.id.reportTitleTextView)
        nameEditText = findViewById(R.id.itemNameEditText)
        descriptionEditText = findViewById(R.id.itemDescriptionEditText)
        categorySpinner = findViewById(R.id.categorySpinner)
        locationEditText = findViewById(R.id.locationEditText)
        dateButton = findViewById(R.id.dateButton)
        keptAtLayout = findViewById(R.id.keptAtLayout)
        keptAtEditText = findViewById(R.id.keptAtEditText)
        itemImageView = findViewById(R.id.itemImageView)
        uploadImageButton = findViewById(R.id.uploadImageButton)
        submitButton = findViewById(R.id.submitButton)
    }

    private fun setupUI() {
        // Set title based on mode
        val titleText = if (editingExistingItem) {
            if (isLostItem) "Edit Lost Item" else "Edit Found Item"
        } else {
            if (isLostItem) "Report Lost Item" else "Report Found Item"
        }
        titleTextView.text = titleText
        submitButton.text = if (editingExistingItem) "Update" else "Submit"

        // Setup category spinner
        val categories = arrayOf(
            "Electronics", "Books & Stationery", "ID Cards & Documents",
            "Keys", "Clothing", "Accessories", "Wallet/Purse", "Other"
        )
        val adapter = ArrayAdapter(this, android.R.layout.simple_spinner_item, categories)
        adapter.setDropDownViewResource(android.R.layout.simple_spinner_dropdown_item)
        categorySpinner.adapter = adapter

        // Show/hide kept at field based on item type
        keptAtLayout.visibility = if (isLostItem) View.GONE else View.VISIBLE

        // Date button setup
        updateDateButtonText()
        dateButton.setOnClickListener {
            showDatePicker()
        }

        // Image upload button
        uploadImageButton.setOnClickListener {
            getContent.launch("image/*")
        }

        // Submit button
        submitButton.setOnClickListener {
            if (validateInputs()) {
                if (imageUri != null && imageUrl.isEmpty()) {
                    uploadImageAndSaveItem()
                } else {
                    saveItem()
                }
            }
        }
    }

    private fun loadItemData() {
        if (editItemId.isNullOrEmpty()) return

        if (isLostItem) {
            itemRepository.getLostItem(editItemId!!).addOnSuccessListener { document ->
                val lostItem = document.toObject(LostItem::class.java) ?: return@addOnSuccessListener

                nameEditText.setText(lostItem.name)
                descriptionEditText.setText(lostItem.description)
                setCategorySpinnerSelection(lostItem.category)
                locationEditText.setText(lostItem.location)
                selectedDate = lostItem.dateLost.toDate()
                updateDateButtonText()

                if (lostItem.imageUrl.isNotEmpty()) {
                    imageUrl = lostItem.imageUrl
                    Glide.with(this)
                        .load(imageUrl)
                        .centerCrop()
                        .into(itemImageView)
                    itemImageView.visibility = View.VISIBLE
                }
            }.addOnFailureListener { e ->
                showErrorDialog("Failed to load item: ${e.message}")
            }
        } else {
            itemRepository.getFoundItem(editItemId!!).addOnSuccessListener { document ->
                val foundItem = document.toObject(FoundItem::class.java) ?: return@addOnSuccessListener

                nameEditText.setText(foundItem.name)
                descriptionEditText.setText(foundItem.description)
                setCategorySpinnerSelection(foundItem.category)
                locationEditText.setText(foundItem.location)
                keptAtEditText.setText(foundItem.keptAt)
                selectedDate = foundItem.dateFound.toDate()
                updateDateButtonText()

                if (foundItem.imageUrl.isNotEmpty()) {
                    imageUrl = foundItem.imageUrl
                    Glide.with(this)
                        .load(imageUrl)
                        .centerCrop()
                        .into(itemImageView)
                    itemImageView.visibility = View.VISIBLE
                }
            }.addOnFailureListener { e ->
                showErrorDialog("Failed to load item: ${e.message}")
            }
        }
    }

    private fun setCategorySpinnerSelection(category: String) {
        val adapter = categorySpinner.adapter as ArrayAdapter<String>
        for (i in 0 until adapter.count) {
            if (adapter.getItem(i) == category) {
                categorySpinner.setSelection(i)
                break
            }
        }
    }

    private fun updateDateButtonText() {
        val dateText = if (isLostItem) {
            "Date Lost: ${dateFormat.format(selectedDate)}"
        } else {
            "Date Found: ${dateFormat.format(selectedDate)}"
        }
        dateButton.text = dateText
    }

    private fun showDatePicker() {
        val datePicker = MaterialDatePicker.Builder.datePicker()
            .setTitleText(if (isLostItem) "Select Lost Date" else "Select Found Date")
            .setSelection(selectedDate.time)
            .build()

        datePicker.addOnPositiveButtonClickListener { selection ->
            selectedDate = Date(selection)
            updateDateButtonText()
        }

        datePicker.show(supportFragmentManager, "DATE_PICKER")
    }

    private fun validateInputs(): Boolean {
        if (nameEditText.text.isBlank()) {
            showErrorDialog("Please enter the item name")
            return false
        }

        if (locationEditText.text.isBlank()) {
            showErrorDialog("Please enter the location")
            return false
        }

        if (!isLostItem && keptAtEditText.text.isBlank()) {
            showErrorDialog("Please enter where the item is kept")
            return false
        }

        if (currentUserId.isEmpty()) {
            showErrorDialog("You must be signed in to report items")
            return false
        // Create a fixed-format filename instead of random UUID
        val timestamp = System.currentTimeMillis()
        val userId = currentUserId.take(8) // Take first 8 chars of user ID for filename
        val imageRef = storageRef.child("item_images/${timestamp}_${userId}.jpg")
        return true
    }
            submitButton.isEnabled = false
            // Show progress to user
            val progressDialog = MaterialAlertDialogBuilder(this)
                .setTitle("Uploading Image")
                .setMessage("Please wait...")
                .setCancelable(false)
                .create()
            progressDialog.show()
            val uploadTask = imageRef.putFile(uri, metadata)

            uploadTask.addOnProgressListener { taskSnapshot: com.google.firebase.storage.UploadTask.TaskSnapshot ->

                progressDialog.setMessage("Uploading: $progress%")
            }
            .addOnSuccessListener { taskSnapshot: com.google.firebase.storage.UploadTask.TaskSnapshot ->
                // Get the download URL only after upload is successful
                imageRef.downloadUrl
                    .addOnSuccessListener { downloadUri: Uri ->
                        progressDialog.dismiss()
                        imageUrl = downloadUri.toString()

                        // Debug log the URL to verify it's correct
                        android.util.Log.d("ReportItemActivity", "Image uploaded successfully: $imageUrl")
            // If somehow we got here without an image, just save the item
                        // Now save the item with the valid image URL
                        saveItem()
            Toast.makeText(this, "Image selected. Saving item...", Toast.LENGTH_SHORT).show()
        }

        // Save the item (with or without image)
        saveItem()
                    }
                    .addOnFailureListener { exception: Exception ->
                        progressDialog.dismiss()
                        submitButton.isEnabled = true
                        submitButton.text = if (editingExistingItem) "Update" else "Submit"

                        // Log the error for debugging
                        android.util.Log.e("ReportItemActivity", "Failed to get download URL: ${exception.message}", exception)
                        showErrorDialog("Failed to get download URL: ${exception.message}")
                    }
            }.addOnFailureListener { exception ->
                showErrorDialog("Failed to upload image: ${exception.message}")
            .addOnFailureListener { exception: Exception ->
                progressDialog.dismiss()
                submitButton.isEnabled = true
                submitButton.text = if (editingExistingItem) "Update" else "Submit"

                // Log the error for debugging
                android.util.Log.e("ReportItemActivity", "Failed to upload image: ${exception.message}", exception)
                showErrorDialog("Failed to upload image: ${exception.message}")
            }
            }.addOnSuccessListener { taskSnapshot ->
                // Get download URL
                imageRef.downloadUrl.addOnSuccessListener { downloadUri ->
                    imageUrl = downloadUri.toString()
                    saveItem()
                }.addOnFailureListener { exception ->
                    showErrorDialog("Failed to get image URL: ${exception.message}")
                }
            }
        } ?: run {
            // No image selected, just save the item
            saveItem()
        }
    }

    private fun saveItem() {
        val name = nameEditText.text.toString().trim()
        val description = descriptionEditText.text.toString().trim()
        val category = categorySpinner.selectedItem.toString()
        val location = locationEditText.text.toString().trim()

        if (isLostItem) {
            val lostItem = LostItem(
                id = editItemId ?: "",
                name = name,
                description = description,
                category = category,
                location = location,
                imageUrl = imageUrl,
                reportedBy = currentUserId,
                reportedByName = currentUserName,
                reportedDate = Timestamp.now(),
                dateLost = Timestamp(selectedDate)
            )

            val task = if (editingExistingItem) {
                itemRepository.updateLostItem(lostItem)
            } else {
                itemRepository.addLostItem(lostItem)
            }

            task.addOnSuccessListener {
                showSuccessDialog(if (editingExistingItem) "Item updated successfully" else "Item reported successfully")
            }.addOnFailureListener { e ->
                showErrorDialog("Failed to save item: ${e.message}")
            }
        } else {
            val keptAt = keptAtEditText.text.toString().trim()

            val foundItem = FoundItem(
                id = editItemId ?: "",
                name = name,
                description = description,
                category = category,
                location = location,
                imageUrl = imageUrl,
                reportedBy = currentUserId,
                reportedByName = currentUserName,
                reportedDate = Timestamp.now(),
                keptAt = keptAt,
                claimed = false,
                claimedBy = "",
                claimedByName = "",
                dateFound = Timestamp(selectedDate)
            )

            val task = if (editingExistingItem) {
                itemRepository.updateFoundItem(foundItem)
            } else {
                itemRepository.addFoundItem(foundItem)
            }

            task.addOnSuccessListener {
                showSuccessDialog(if (editingExistingItem) "Item updated successfully" else "Item reported successfully")
            }.addOnFailureListener { e ->
                showErrorDialog("Failed to save item: ${e.message}")
            }
        }
    }

    private fun showSuccessDialog(message: String) {
        MaterialAlertDialogBuilder(this)
            .setTitle("Success")
            .setMessage(message)
            .setPositiveButton("OK") { _, _ ->
                setResult(Activity.RESULT_OK)
                finish()
            }
            .setCancelable(false)
            .show()
    }

    private fun showErrorDialog(message: String) {
        MaterialAlertDialogBuilder(this)
            .setTitle("Error")
            .setMessage(message)
            .setPositiveButton("OK", null)
            .show()
    }

    companion object {
        fun createEditIntent(context: Context, isLostItem: Boolean, itemId: String): Intent {
            return Intent(context, ReportItemActivity::class.java).apply {
                putExtra("isLostItem", isLostItem)
                putExtra("itemId", itemId)
            }
        }
    }
}

package com.example.campus_lost_found

import android.app.Activity
import android.content.Context
import android.content.Intent
import android.net.Uri
import android.os.Bundle
import android.view.View
import android.widget.*
import androidx.activity.result.contract.ActivityResultContracts
import androidx.appcompat.app.AppCompatActivity
import com.bumptech.glide.Glide
import com.example.campus_lost_found.model.FoundItem
import com.example.campus_lost_found.model.LostItem
import com.example.campus_lost_found.repository.ItemRepository
import com.google.android.material.datepicker.MaterialDatePicker
import com.google.android.material.dialog.MaterialAlertDialogBuilder
import com.google.firebase.Timestamp
import com.google.firebase.auth.FirebaseAuth
import com.google.firebase.storage.FirebaseStorage
import com.google.firebase.storage.StorageException
import com.google.firebase.storage.UploadTask
import java.text.SimpleDateFormat
import java.util.Date
import java.util.Locale

class ReportItemActivity : AppCompatActivity() {

    private lateinit var titleTextView: TextView
    private lateinit var nameEditText: EditText
    private lateinit var descriptionEditText: EditText
    private lateinit var categorySpinner: Spinner
    private lateinit var locationEditText: EditText
    private lateinit var dateButton: Button
    private lateinit var keptAtLayout: View
    private lateinit var keptAtEditText: EditText
    private lateinit var itemImageView: ImageView
    private lateinit var uploadImageButton: Button
    private lateinit var submitButton: Button

    private val itemRepository = ItemRepository()
    private val storage = FirebaseStorage.getInstance()
    private val dateFormat = SimpleDateFormat("MMM dd, yyyy", Locale.getDefault())

    private var isLostItem = true
    private var selectedDate: Date = Date()
    private var imageUri: Uri? = null
    private var imageUrl: String = ""
    private var editItemId: String? = null
    private var editingExistingItem = false

    private val currentUserId: String
        get() = FirebaseAuth.getInstance().currentUser?.uid ?: ""
    private val currentUserName: String
        get() = FirebaseAuth.getInstance().currentUser?.displayName ?: "Anonymous User"

    private val getContent = registerForActivityResult(ActivityResultContracts.GetContent()) { uri ->
        uri?.let {
            imageUri = it
            Glide.with(this)
                .load(uri)
                .centerCrop()
                .into(itemImageView)
            itemImageView.visibility = View.VISIBLE
        }
    }

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_report_item)

        // Get intent data
        isLostItem = intent.getBooleanExtra("isLostItem", true)
        editItemId = intent.getStringExtra("itemId")
        editingExistingItem = editItemId != null

        // Initialize views
        initViews()
        setupUI()

        // Load item data if editing
        if (editingExistingItem) {
            loadItemData()
        }
    }

    private fun initViews() {
        titleTextView = findViewById(R.id.reportTitleTextView)
        nameEditText = findViewById(R.id.itemNameEditText)
        descriptionEditText = findViewById(R.id.itemDescriptionEditText)
        categorySpinner = findViewById(R.id.categorySpinner)
        locationEditText = findViewById(R.id.locationEditText)
        dateButton = findViewById(R.id.dateButton)
        keptAtLayout = findViewById(R.id.keptAtLayout)
        keptAtEditText = findViewById(R.id.keptAtEditText)
        itemImageView = findViewById(R.id.itemImageView)
        uploadImageButton = findViewById(R.id.uploadImageButton)
        submitButton = findViewById(R.id.submitButton)
    }

    private fun setupUI() {
        // Set title based on mode
        val titleText = if (editingExistingItem) {
            if (isLostItem) "Edit Lost Item" else "Edit Found Item"
        } else {
            if (isLostItem) "Report Lost Item" else "Report Found Item"
        }
        titleTextView.text = titleText
        submitButton.text = if (editingExistingItem) "Update" else "Submit"

        // Setup category spinner
        val categories = arrayOf(
            "Electronics", "Books & Stationery", "ID Cards & Documents",
            "Keys", "Clothing", "Accessories", "Wallet/Purse", "Other"
        )
        val adapter = ArrayAdapter(this, android.R.layout.simple_spinner_item, categories)
        adapter.setDropDownViewResource(android.R.layout.simple_spinner_dropdown_item)
        categorySpinner.adapter = adapter

        // Show/hide kept at field based on item type
        keptAtLayout.visibility = if (isLostItem) View.GONE else View.VISIBLE

        // Date button setup
        updateDateButtonText()
        dateButton.setOnClickListener {
            showDatePicker()
        }

        // Image upload button
        uploadImageButton.setOnClickListener {
            getContent.launch("image/*")
        }

        // Submit button
        submitButton.setOnClickListener {
            if (validateInputs()) {
                if (imageUri != null && imageUrl.isEmpty()) {
                    uploadImageAndSaveItem()
                } else {
                    saveItem()
                }
            }
        }
    }

    private fun loadItemData() {
        if (editItemId.isNullOrEmpty()) return

        if (isLostItem) {
            itemRepository.getLostItem(editItemId!!).addOnSuccessListener { document ->
                val lostItem = document.toObject(LostItem::class.java) ?: return@addOnSuccessListener

                nameEditText.setText(lostItem.name)
                descriptionEditText.setText(lostItem.description)
                setCategorySpinnerSelection(lostItem.category)
                locationEditText.setText(lostItem.location)
                selectedDate = lostItem.dateLost.toDate()
                updateDateButtonText()

                if (lostItem.imageUrl.isNotEmpty()) {
                    imageUrl = lostItem.imageUrl
                    Glide.with(this)
                        .load(imageUrl)
                        .centerCrop()
                        .into(itemImageView)
                    itemImageView.visibility = View.VISIBLE
                }
            }.addOnFailureListener { e ->
                showErrorDialog("Failed to load item: ${e.message}")
            }
        } else {
            itemRepository.getFoundItem(editItemId!!).addOnSuccessListener { document ->
                val foundItem = document.toObject(FoundItem::class.java) ?: return@addOnSuccessListener

                nameEditText.setText(foundItem.name)
                descriptionEditText.setText(foundItem.description)
                setCategorySpinnerSelection(foundItem.category)
                locationEditText.setText(foundItem.location)
                keptAtEditText.setText(foundItem.keptAt)
                selectedDate = foundItem.dateFound.toDate()
                updateDateButtonText()

                if (foundItem.imageUrl.isNotEmpty()) {
                    imageUrl = foundItem.imageUrl
                    Glide.with(this)
                        .load(imageUrl)
                        .centerCrop()
                        .into(itemImageView)
                    itemImageView.visibility = View.VISIBLE
                }
            }.addOnFailureListener { e ->
                showErrorDialog("Failed to load item: ${e.message}")
            }
        }
    }

    private fun setCategorySpinnerSelection(category: String) {
        val adapter = categorySpinner.adapter as ArrayAdapter<String>
        for (i in 0 until adapter.count) {
            if (adapter.getItem(i) == category) {
                categorySpinner.setSelection(i)
                break
            }
        }
    }

    private fun updateDateButtonText() {
        val dateText = if (isLostItem) {
            "Date Lost: ${dateFormat.format(selectedDate)}"
        } else {
            "Date Found: ${dateFormat.format(selectedDate)}"
        }
        dateButton.text = dateText
    }

    private fun showDatePicker() {
        val datePicker = MaterialDatePicker.Builder.datePicker()
            .setTitleText(if (isLostItem) "Select Lost Date" else "Select Found Date")
            .setSelection(selectedDate.time)
            .build()

        datePicker.addOnPositiveButtonClickListener { selection ->
            selectedDate = Date(selection)
            updateDateButtonText()
        }

        datePicker.show(supportFragmentManager, "DATE_PICKER")
    }

    private fun validateInputs(): Boolean {
        if (nameEditText.text.isBlank()) {
            showErrorDialog("Please enter the item name")
            return false
        }

        if (locationEditText.text.isBlank()) {
            showErrorDialog("Please enter the location")
            return false
        }

        if (!isLostItem && keptAtEditText.text.isBlank()) {
            showErrorDialog("Please enter where the item is kept")
            return false
        }

        if (currentUserId.isEmpty()) {
            showErrorDialog("You must be signed in to report items")
            return false
        }

        return true
    }

    private fun uploadImageAndSaveItem() {
        imageUri?.let { uri ->
            val filename = "images/${System.currentTimeMillis()}_${currentUserId}.jpg"
            val imageRef = storage.reference.child(filename)

            val uploadTask = imageRef.putFile(uri)

            // Show progress
            Toast.makeText(this, "Uploading image...", Toast.LENGTH_SHORT).show()

            uploadTask.addOnProgressListener { taskSnapshot: UploadTask.TaskSnapshot ->
                val progress = (100.0 * taskSnapshot.bytesTransferred / taskSnapshot.totalByteCount).toInt()
                // You could update a progress bar here if you have one
            }.addOnFailureListener { exception: Exception ->
                showErrorDialog("Failed to upload image: ${exception.message}")
            }.addOnSuccessListener { taskSnapshot: UploadTask.TaskSnapshot ->
                // Get download URL
                imageRef.downloadUrl.addOnSuccessListener { downloadUri ->
                    imageUrl = downloadUri.toString()
                    saveItem()
                }.addOnFailureListener { exception: Exception ->
                    showErrorDialog("Failed to get image URL: ${exception.message}")
                }
            }
        } ?: run {
            // No image selected, just save the item
            saveItem()
        }
    }

    private fun saveItem() {
        val name = nameEditText.text.toString().trim()
        val description = descriptionEditText.text.toString().trim()
        val category = categorySpinner.selectedItem.toString()
        val location = locationEditText.text.toString().trim()

        if (isLostItem) {
            val lostItem = LostItem(
                id = editItemId ?: "",
                name = name,
                description = description,
                category = category,
                location = location,
                imageUrl = imageUrl,
                reportedBy = currentUserId,
                reportedByName = currentUserName,
                reportedDate = Timestamp.now(),
                dateLost = Timestamp(selectedDate)
            )

            val task = if (editingExistingItem) {
                itemRepository.updateLostItem(lostItem)
            } else {
                itemRepository.addLostItem(lostItem)
            }

            task.addOnSuccessListener {
                showSuccessDialog(if (editingExistingItem) "Item updated successfully" else "Item reported successfully")
            }.addOnFailureListener { e ->
                showErrorDialog("Failed to save item: ${e.message}")
            }
        } else {
            val keptAt = keptAtEditText.text.toString().trim()

            val foundItem = FoundItem(
                id = editItemId ?: "",
                name = name,
                description = description,
                category = category,
                location = location,
                imageUrl = imageUrl,
                reportedBy = currentUserId,
                reportedByName = currentUserName,
                reportedDate = Timestamp.now(),
                keptAt = keptAt,
                claimed = false,
                claimedBy = "",
                claimedByName = "",
                dateFound = Timestamp(selectedDate)
            )

            val task = if (editingExistingItem) {
                itemRepository.updateFoundItem(foundItem)
            } else {
                itemRepository.addFoundItem(foundItem)
            }

            task.addOnSuccessListener {
                showSuccessDialog(if (editingExistingItem) "Item updated successfully" else "Item reported successfully")
            }.addOnFailureListener { e ->
                showErrorDialog("Failed to save item: ${e.message}")
            }
        }
    }

    private fun showSuccessDialog(message: String) {
        MaterialAlertDialogBuilder(this)
            .setTitle("Success")
            .setMessage(message)
            .setPositiveButton("OK") { _, _ ->
                setResult(Activity.RESULT_OK)
                finish()
            }
            .setCancelable(false)
            .show()
    }

    private fun showErrorDialog(message: String) {
        MaterialAlertDialogBuilder(this)
            .setTitle("Error")
            .setMessage(message)
            .setPositiveButton("OK", null)
            .show()
    }

    companion object {
        fun createEditIntent(context: Context, isLostItem: Boolean, itemId: String): Intent {
            return Intent(context, ReportItemActivity::class.java).apply {
                putExtra("isLostItem", isLostItem)
                putExtra("itemId", itemId)
            }
        }
    }
}

package com.example.campus_lost_found.adapter

import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.Button
import android.widget.ImageView
import android.widget.TextView
import androidx.recyclerview.widget.DiffUtil
import androidx.recyclerview.widget.RecyclerView
import com.bumptech.glide.Glide
import com.example.campus_lost_found.R
import com.example.campus_lost_found.model.FoundItem
import com.example.campus_lost_found.model.Item
import com.example.campus_lost_found.model.LostItem
import java.text.SimpleDateFormat
import java.util.Locale

class ItemsAdapter(
    private var items: MutableList<Item> = mutableListOf(),
    private val isLostItemsList: Boolean,
    private val currentUserId: String,
    private val onClaimButtonClick: ((Item) -> Unit)? = null,
    private val onItemClick: ((Item) -> Unit)? = null
) : RecyclerView.Adapter<ItemsAdapter.ItemViewHolder>() {

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ItemViewHolder {
        val view = LayoutInflater.from(parent.context).inflate(R.layout.item_card, parent, false)
        return ItemViewHolder(view)
    }

    override fun onBindViewHolder(holder: ItemViewHolder, position: Int) {
        val item = items[position]
        holder.bind(item, isLostItemsList, currentUserId, onClaimButtonClick, onItemClick)
    }

    override fun getItemCount() = items.size

    // Method to update the items list efficiently
    fun updateItems(newItems: List<Item>) {
        val diffCallback = ItemDiffCallback(items, newItems)
        val diffResult = DiffUtil.calculateDiff(diffCallback)

        items.clear()
        items.addAll(newItems)
        diffResult.dispatchUpdatesTo(this)
    }

    // DiffUtil callback for efficient list updates
    private class ItemDiffCallback(
        private val oldList: List<Item>,
        private val newList: List<Item>
    ) : DiffUtil.Callback() {

        override fun getOldListSize(): Int = oldList.size

        override fun getNewListSize(): Int = newList.size

        override fun areItemsTheSame(oldItemPosition: Int, newItemPosition: Int): Boolean {
            return oldList[oldItemPosition].id == newList[newItemPosition].id
        }

        override fun areContentsTheSame(oldItemPosition: Int, newItemPosition: Int): Boolean {
            return oldList[oldItemPosition] == newList[newItemPosition]
        }
    }

    class ItemViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {
        private val itemImage: ImageView = itemView.findViewById(R.id.itemImage)
        private val itemName: TextView = itemView.findViewById(R.id.itemName)
        private val itemDescription: TextView = itemView.findViewById(R.id.itemDescription)
        private val itemCategory: TextView = itemView.findViewById(R.id.itemCategory)
        private val itemLocation: TextView = itemView.findViewById(R.id.itemLocation)
        private val itemDate: TextView = itemView.findViewById(R.id.itemDate)
        private val claimButton: Button = itemView.findViewById(R.id.claimButton)

        fun bind(
            item: Item,
            isLostItemsList: Boolean,
            currentUserId: String,
            onClaimButtonClick: ((Item) -> Unit)?,
            onItemClick: ((Item) -> Unit)?
        ) {
            itemName.text = item.name
            itemDescription.text = item.description
            itemCategory.text = item.category

            // Format date
            val dateFormat = SimpleDateFormat("MMM dd, yyyy", Locale.getDefault())

            when (item) {
                is LostItem -> {
                    itemLocation.text = itemView.context.getString(R.string.lost_at, item.location)
                    itemDate.text = itemView.context.getString(R.string.lost_on, dateFormat.format(item.date))
                }
                is FoundItem -> {
                    itemLocation.text = itemView.context.getString(R.string.found_at, item.location)
                    itemDate.text = itemView.context.getString(R.string.found_on, dateFormat.format(item.date))
                }
            }

            // Load image with Glide
            if (!item.imageUrl.isNullOrEmpty()) {
                Glide.with(itemView.context)
                    .load(item.imageUrl)
                    .placeholder(R.drawable.ic_placeholder)
                    .error(R.drawable.ic_error)
                    .into(itemImage)
            } else {
                itemImage.setImageResource(R.drawable.ic_placeholder)
            }

            // Set up claim button
            if (item.userId == currentUserId) {
                claimButton.visibility = View.GONE
            } else {
                claimButton.visibility = View.VISIBLE
                claimButton.text = if (isLostItemsList) {
                    itemView.context.getString(R.string.i_found_this)
                } else {
                    itemView.context.getString(R.string.this_is_mine)
                }
                claimButton.setOnClickListener {
                    onClaimButtonClick?.invoke(item)
                }
            }

            // Set up item click
            itemView.setOnClickListener {
                onItemClick?.invoke(item)
            }
        }
    }
}

package com.example.campus_lost_found.adapter

import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.Button
import android.widget.ImageView
import android.widget.TextView
import androidx.recyclerview.widget.DiffUtil
import androidx.recyclerview.widget.RecyclerView
import com.bumptech.glide.Glide
import com.example.campus_lost_found.R
import com.example.campus_lost_found.model.FoundItem
import com.example.campus_lost_found.model.Item
import com.example.campus_lost_found.model.LostItem
import java.text.SimpleDateFormat
import java.util.Locale

class ItemsAdapter(
    private var items: MutableList<Item> = mutableListOf(),
    private val isLostItemsList: Boolean,
    private val currentUserId: String,
    private val onClaimButtonClick: ((Item) -> Unit)? = null,
    private val onItemClick: ((Item) -> Unit)? = null
) : RecyclerView.Adapter<ItemsAdapter.ItemViewHolder>() {

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ItemViewHolder {
        val view = LayoutInflater.from(parent.context).inflate(R.layout.item_card, parent, false)
        return ItemViewHolder(view)
    }

    override fun onBindViewHolder(holder: ItemViewHolder, position: Int) {
        val item = items[position]
        holder.bind(item, isLostItemsList, currentUserId, onClaimButtonClick, onItemClick)
    }

    override fun getItemCount() = items.size

    // Method to update the items list efficiently
    fun updateItems(newItems: List<Item>) {
        val diffCallback = ItemDiffCallback(items, newItems)
        val diffResult = DiffUtil.calculateDiff(diffCallback)

        items.clear()
        items.addAll(newItems)
        diffResult.dispatchUpdatesTo(this)
    }

    // DiffUtil callback for efficient list updates
    private class ItemDiffCallback(
        private val oldList: List<Item>,
        private val newList: List<Item>
    ) : DiffUtil.Callback() {

        override fun getOldListSize(): Int = oldList.size

        override fun getNewListSize(): Int = newList.size

        override fun areItemsTheSame(oldItemPosition: Int, newItemPosition: Int): Boolean {
            return oldList[oldItemPosition].id == newList[newItemPosition].id
        }

        override fun areContentsTheSame(oldItemPosition: Int, newItemPosition: Int): Boolean {
            return oldList[oldItemPosition] == newList[newItemPosition]
        }
    }

    class ItemViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {
        private val itemImage: ImageView = itemView.findViewById(R.id.itemImage)
        private val itemName: TextView = itemView.findViewById(R.id.itemName)
        private val itemDescription: TextView = itemView.findViewById(R.id.itemDescription)
        private val itemCategory: TextView = itemView.findViewById(R.id.itemCategory)
        private val itemLocation: TextView = itemView.findViewById(R.id.itemLocation)
        private val itemDate: TextView = itemView.findViewById(R.id.itemDate)
        private val claimButton: Button = itemView.findViewById(R.id.claimButton)

        fun bind(
            item: Item,
            isLostItemsList: Boolean,
            currentUserId: String,
            onClaimButtonClick: ((Item) -> Unit)?,
            onItemClick: ((Item) -> Unit)?
        ) {
            itemName.text = item.name
            itemDescription.text = item.description
            itemCategory.text = item.category

            // Format date
            val dateFormat = SimpleDateFormat("MMM dd, yyyy", Locale.getDefault())

            when (item) {
                is LostItem -> {
                    itemLocation.text = itemView.context.getString(R.string.lost_at, item.location)
                    itemDate.text = itemView.context.getString(R.string.lost_on, dateFormat.format(item.dateLost.toDate()))
                }
                is FoundItem -> {
                    itemLocation.text = itemView.context.getString(R.string.found_at, item.location)
                    itemDate.text = itemView.context.getString(R.string.found_on, dateFormat.format(item.dateFound.toDate()))
                }
            }

            // Load image with Glide
            if (item.imageUrl.isNotEmpty()) {
                Glide.with(itemView.context)
                    .load(item.imageUrl)
                    .placeholder(R.drawable.ic_placeholder)
                    .error(R.drawable.ic_error)
                    .into(itemImage)
            } else {
                itemImage.setImageResource(R.drawable.ic_placeholder)
            }

            // Set up claim button
            if (item.reportedBy == currentUserId) {
                claimButton.visibility = View.GONE
            } else {
                claimButton.visibility = View.VISIBLE
                claimButton.text = if (isLostItemsList) {
                    itemView.context.getString(R.string.i_found_this)
                } else {
                    itemView.context.getString(R.string.this_is_mine)
                }
                claimButton.setOnClickListener {
                    onClaimButtonClick?.invoke(item)
                }
            }

            // Set up item click
            itemView.setOnClickListener {
                onItemClick?.invoke(item)
            }
        }
    }
}

package com.example.campus_lost_found.utils

import android.app.DatePickerDialog
import android.content.Context
import android.text.Editable
import android.text.TextWatcher
import android.view.LayoutInflater
import android.view.View
import android.widget.ArrayAdapter
import android.widget.EditText
import android.widget.Spinner
import androidx.appcompat.app.AlertDialog
import com.example.campus_lost_found.R
import com.google.android.material.button.MaterialButton
import com.google.android.material.chip.Chip
import com.google.android.material.chip.ChipGroup
import com.google.android.material.dialog.MaterialAlertDialogBuilder
import com.google.android.material.textfield.TextInputEditText
import com.google.firebase.Timestamp
import java.text.SimpleDateFormat
import java.util.Calendar
import java.util.Date
import java.util.Locale

class SearchFilterManager(
    private val context: Context,
    private val rootView: View,
    private val onSearchFiltersChanged: (
        query: String,
        categories: List<String>,
        startDate: Date?,
        endDate: Date?,
        location: String?
    ) -> Unit
) {
    private val searchEditText: TextInputEditText = rootView.findViewById(R.id.search_edit_text)
    private val categoryChipGroup: ChipGroup = rootView.findViewById(R.id.category_chip_group)
    private val dateFilterButton: MaterialButton = rootView.findViewById(R.id.date_filter_button)
    private val locationFilterButton: MaterialButton = rootView.findViewById(R.id.location_filter_button)

    private var searchQuery: String = ""
    private val selectedCategories = mutableListOf<String>()
    private var startDate: Date? = null
    private var endDate: Date? = null
    private var selectedLocation: String? = null

    // Pre-defined campus locations
    private val campusLocations = listOf(
        "Library", "Student Center", "Academic Building", "Cafeteria", 
        "Gymnasium", "Dormitory", "Parking Lot", "Auditorium", "Labs"
    )

    init {
        setupSearchListener()
        setupCategoryChips()
        setupDateFilter()
        setupLocationFilter()
    }

    private fun setupSearchListener() {
        searchEditText.addTextChangedListener(object : TextWatcher {
            override fun beforeTextChanged(s: CharSequence?, start: Int, count: Int, after: Int) {}
            override fun onTextChanged(s: CharSequence?, start: Int, before: Int, count: Int) {}
            override fun afterTextChanged(s: Editable?) {
                searchQuery = s?.toString()?.trim() ?: ""
                notifyFiltersChanged()
            }
        })
    }

    private fun setupCategoryChips() {
        // Set up click listeners for all category chips
        for (i in 0 until categoryChipGroup.childCount) {
            val chip = categoryChipGroup.getChildAt(i) as Chip
            chip.setOnCheckedChangeListener { _, isChecked ->
                val category = chip.text.toString()
                if (isChecked) {
                    if (!selectedCategories.contains(category)) {
                        selectedCategories.add(category)
                    }
                } else {
                    selectedCategories.remove(category)
                }
                notifyFiltersChanged()
            }
        }
    }

    private fun setupDateFilter() {
        val dateFormat = SimpleDateFormat("MMM dd, yyyy", Locale.getDefault())

        dateFilterButton.setOnClickListener {
            // Create a dialog for date range selection
            val dialogView = LayoutInflater.from(context).inflate(R.layout.dialog_date_range, null)
            val startDateButton = dialogView.findViewById<MaterialButton>(R.id.start_date_button)
            val endDateButton = dialogView.findViewById<MaterialButton>(R.id.end_date_button)
            val clearButton = dialogView.findViewById<MaterialButton>(R.id.clear_date_button)

            // Initialize with current values
            startDateButton.text = startDate?.let { dateFormat.format(it) } ?: "Select Start Date"
            endDateButton.text = endDate?.let { dateFormat.format(it) } ?: "Select End Date"

            // Start date button - use MaterialDatePicker for better calendar selection
            startDateButton.setOnClickListener {
                val builder = com.google.android.material.datepicker.MaterialDatePicker.Builder.datePicker()
                    .setTitleText("Select Start Date")

                // If there's already a start date, select it in the picker
                if (startDate != null) {
                    builder.setSelection(startDate!!.time)
                }

                val datePicker = builder.build()

                datePicker.addOnPositiveButtonClickListener { selection ->
                    startDate = Date(selection)
                    startDateButton.text = dateFormat.format(startDate!!)
                }

                // Show the calendar picker
                datePicker.show((context as androidx.fragment.app.FragmentActivity).supportFragmentManager, "START_DATE_PICKER")
            }

            // End date button - use MaterialDatePicker for better calendar selection
            endDateButton.setOnClickListener {
                val builder = com.google.android.material.datepicker.MaterialDatePicker.Builder.datePicker()
                    .setTitleText("Select End Date")

                // If there's already an end date, select it in the picker
                if (endDate != null) {
                    builder.setSelection(endDate!!.time)
                }

                val datePicker = builder.build()

                datePicker.addOnPositiveButtonClickListener { selection ->
                    endDate = Date(selection)
                    endDateButton.text = dateFormat.format(endDate!!)
                }

                // Show the calendar picker
                datePicker.show((context as androidx.fragment.app.FragmentActivity).supportFragmentManager, "END_DATE_PICKER")
            }

            clearButton.setOnClickListener {
                startDate = null
                endDate = null
                startDateButton.text = "Select Start Date"
                endDateButton.text = "Select End Date"
            }

            // Show dialog
            MaterialAlertDialogBuilder(context)
                .setTitle("Select Date Range")
                .setView(dialogView)
                .setPositiveButton("Apply") { _, _ ->
                    updateDateFilterButtonText()
                    notifyFiltersChanged()
                }
                .setNegativeButton("Cancel", null)
                .show()
        }
    }

    private fun setupLocationFilter() {
        locationFilterButton.setOnClickListener {
            val dialogView = LayoutInflater.from(context).inflate(R.layout.dialog_location_filter, null)
            val locationSpinner = dialogView.findViewById<Spinner>(R.id.location_spinner)
            val locationEditText = dialogView.findViewById<EditText>(R.id.location_edit_text)

            // Set up the spinner with campus locations
            val adapter = ArrayAdapter(context, android.R.layout.simple_spinner_item, 
                listOf("Select location...") + campusLocations + "Other")
            adapter.setDropDownViewResource(android.R.layout.simple_spinner_dropdown_item)
            locationSpinner.adapter = adapter

            // Set initial selection based on current filter
            if (selectedLocation != null) {
                val position = campusLocations.indexOf(selectedLocation)
                if (position >= 0) {
                    locationSpinner.setSelection(position + 1) // +1 for the "Select location..." item
                } else {
                    locationSpinner.setSelection(adapter.count - 1) // "Other" option
                    locationEditText.setText(selectedLocation)
                    locationEditText.visibility = View.VISIBLE
                }
            }

            // Show/hide the EditText based on the "Other" selection
            locationSpinner.setOnItemSelectedListener(object : android.widget.AdapterView.OnItemSelectedListener {
                override fun onItemSelected(parent: android.widget.AdapterView<*>?, view: View?, position: Int, id: Long) {
                    locationEditText.visibility = if (position == adapter.count - 1) View.VISIBLE else View.GONE
                }
                override fun onNothingSelected(parent: android.widget.AdapterView<*>?) {}
            })

            // Show dialog
            MaterialAlertDialogBuilder(context)
                .setTitle("Filter by Location")
                .setView(dialogView)
                .setPositiveButton("Apply") { _, _ ->
                    val position = locationSpinner.selectedItemPosition
                    selectedLocation = when {
                        position == 0 -> null // "Select location..."
                        position < adapter.count - 1 -> adapter.getItem(position).toString() // Campus locations
                        else -> locationEditText.text.toString().takeIf { it.isNotBlank() } // "Other" with custom input
                    }
                    updateLocationFilterButtonText()
                    notifyFiltersChanged()
                }
                .setNegativeButton("Clear") { _, _ ->
                    selectedLocation = null
                    updateLocationFilterButtonText()
                    notifyFiltersChanged()
                }
                .show()
        }
    }

    private fun updateDateFilterButtonText() {
        val dateFormat = SimpleDateFormat("MM/dd", Locale.getDefault())
        dateFilterButton.text = when {
            startDate != null && endDate != null -> 
                "${dateFormat.format(startDate!!)} - ${dateFormat.format(endDate!!)}"
            startDate != null -> 
                "From ${dateFormat.format(startDate!!)}"
            endDate != null -> 
                "Until ${dateFormat.format(endDate!!)}"
            else -> 
                "Date Range"
        }
    }

    private fun updateLocationFilterButtonText() {
        locationFilterButton.text = selectedLocation ?: "Location"
    }

    private fun notifyFiltersChanged() {
        onSearchFiltersChanged(searchQuery, selectedCategories, startDate, endDate, selectedLocation)
    }

    // Utility method to convert Date to Firestore Timestamp
    fun getStartTimestamp(): Timestamp? = startDate?.let { Timestamp(it) }
    fun getEndTimestamp(): Timestamp? = endDate?.let { Timestamp(it) }
}

package com.example.campus_lost_found.utils

import android.app.DatePickerDialog
import android.content.Context
import android.text.Editable
import android.text.TextWatcher
import android.view.LayoutInflater
import android.view.View
import android.widget.ArrayAdapter
import android.widget.EditText
import android.widget.Spinner
import androidx.appcompat.app.AlertDialog
import com.example.campus_lost_found.R
import com.google.android.material.button.MaterialButton
import com.google.android.material.chip.Chip
import com.google.android.material.chip.ChipGroup
import com.google.android.material.dialog.MaterialAlertDialogBuilder
import com.google.android.material.textfield.TextInputEditText
import com.google.firebase.Timestamp
import java.text.SimpleDateFormat
import java.util.Calendar
import java.util.Date
import java.util.Locale

class SearchFilterManager(
    private val context: Context,
    private val rootView: View,
    private val onSearchFiltersChanged: (
        query: String,
        categories: List<String>,
        startDate: Date?,
        endDate: Date?,
        location: String?
    ) -> Unit
) {
    private val searchEditText: TextInputEditText = rootView.findViewById(R.id.search_edit_text)
    private val categoryChipGroup: ChipGroup = rootView.findViewById(R.id.category_chip_group)
    private val dateFilterButton: MaterialButton = rootView.findViewById(R.id.date_filter_button)
    private val locationFilterButton: MaterialButton = rootView.findViewById(R.id.location_filter_button)

    private var searchQuery: String = ""
    private val selectedCategories = mutableListOf<String>()
    private var startDate: Date? = null
    private var endDate: Date? = null
    private var selectedLocation: String? = null

    // Pre-defined campus locations
    private val campusLocations = listOf(
        "Library", "Student Center", "Academic Building", "Cafeteria", 
        "Gymnasium", "Dormitory", "Parking Lot", "Auditorium", "Labs"
    )

    init {
        setupSearchListener()
        setupCategoryChips()
        setupDateFilter()
        setupLocationFilter()
    }

    private fun setupSearchListener() {
        searchEditText.addTextChangedListener(object : TextWatcher {
            override fun beforeTextChanged(s: CharSequence?, start: Int, count: Int, after: Int) {}
            override fun onTextChanged(s: CharSequence?, start: Int, before: Int, count: Int) {}
            override fun afterTextChanged(s: Editable?) {
                searchQuery = s?.toString()?.trim() ?: ""
                notifyFiltersChanged()
            }
        })
    }

    private fun setupCategoryChips() {
        // Set up click listeners for all category chips
        for (i in 0 until categoryChipGroup.childCount) {
            val chip = categoryChipGroup.getChildAt(i) as Chip
            chip.setOnCheckedChangeListener { _, isChecked ->
                val category = chip.text.toString()
                if (isChecked) {
                    if (!selectedCategories.contains(category)) {
                        selectedCategories.add(category)
                    }
                } else {
                    selectedCategories.remove(category)
                }
                notifyFiltersChanged()
            }
        }
    }

    private fun setupDateFilter() {
        val dateFormat = SimpleDateFormat("MMM dd, yyyy", Locale.getDefault())

        dateFilterButton.setOnClickListener {
            // Create a dialog for date range selection
            val dialogView = LayoutInflater.from(context).inflate(R.layout.dialog_date_range, null)
            val startDateButton = dialogView.findViewById<MaterialButton>(R.id.start_date_button)
            val endDateButton = dialogView.findViewById<MaterialButton>(R.id.end_date_button)
            val clearButton = dialogView.findViewById<MaterialButton>(R.id.clear_date_button)

            // Initialize with current values
            startDateButton.text = startDate?.let { dateFormat.format(it) } ?: "Select Start Date"
            endDateButton.text = endDate?.let { dateFormat.format(it) } ?: "Select End Date"

            // Start date button - use MaterialDatePicker for better calendar selection
            startDateButton.setOnClickListener {
                val builder = com.google.android.material.datepicker.MaterialDatePicker.Builder.datePicker()
                    .setTitleText("Select Start Date")

                // If there's already a start date, select it in the picker
                if (startDate != null) {
                    builder.setSelection(startDate!!.time)
                }

                val datePicker = builder.build()

                datePicker.addOnPositiveButtonClickListener { selection ->
                    startDate = Date(selection)
                    startDateButton.text = dateFormat.format(startDate!!)
                }

                // Show the calendar picker
                datePicker.show((context as androidx.fragment.app.FragmentActivity).supportFragmentManager, "START_DATE_PICKER")
            }

            // End date button - use MaterialDatePicker for better calendar selection
            endDateButton.setOnClickListener {
                val builder = com.google.android.material.datepicker.MaterialDatePicker.Builder.datePicker()
                    .setTitleText("Select End Date")

                // If there's already an end date, select it in the picker
                if (endDate != null) {
                    builder.setSelection(endDate!!.time)
                }

                val datePicker = builder.build()

                datePicker.addOnPositiveButtonClickListener { selection ->
                    endDate = Date(selection)
                    endDateButton.text = dateFormat.format(endDate!!)
                }

                // Show the calendar picker
                datePicker.show((context as androidx.fragment.app.FragmentActivity).supportFragmentManager, "END_DATE_PICKER")
            }

            clearButton.setOnClickListener {
                startDate = null
                endDate = null
                startDateButton.text = "Select Start Date"
                endDateButton.text = "Select End Date"
            }

            // Show dialog
            MaterialAlertDialogBuilder(context)
                .setTitle("Select Date Range")
                .setView(dialogView)
                .setPositiveButton("Apply") { _, _ ->
                    updateDateFilterButtonText()
                    notifyFiltersChanged()
                }
                .setNegativeButton("Cancel", null)
                .show()
        }
    }

    private fun setupLocationFilter() {
        locationFilterButton.setOnClickListener {
            val dialogView = LayoutInflater.from(context).inflate(R.layout.dialog_location_filter, null)
            val locationSpinner = dialogView.findViewById<Spinner>(R.id.location_spinner)
            val locationEditText = dialogView.findViewById<EditText>(R.id.location_edit_text)

            // Set up the spinner with campus locations
            val adapter = ArrayAdapter(context, android.R.layout.simple_spinner_item, 
                listOf("Select location...") + campusLocations + "Other")
            adapter.setDropDownViewResource(android.R.layout.simple_spinner_dropdown_item)
            locationSpinner.adapter = adapter

            // Set initial selection based on current filter
            if (selectedLocation != null) {
                val position = campusLocations.indexOf(selectedLocation)
                if (position >= 0) {
                    locationSpinner.setSelection(position + 1) // +1 for the "Select location..." item
                } else {
                    locationSpinner.setSelection(adapter.count - 1) // "Other" option
                    locationEditText.setText(selectedLocation)
                    locationEditText.visibility = View.VISIBLE
                }
            }

            // Show/hide the EditText based on the "Other" selection
            locationSpinner.setOnItemSelectedListener(object : android.widget.AdapterView.OnItemSelectedListener {
                override fun onItemSelected(parent: android.widget.AdapterView<*>?, view: View?, position: Int, id: Long) {
                    locationEditText.visibility = if (position == adapter.count - 1) View.VISIBLE else View.GONE
                }
                override fun onNothingSelected(parent: android.widget.AdapterView<*>?) {}
            })

            // Show dialog
            MaterialAlertDialogBuilder(context)
                .setTitle("Filter by Location")
                .setView(dialogView)
                .setPositiveButton("Apply") { _, _ ->
                    val position = locationSpinner.selectedItemPosition
                    selectedLocation = when {
                        position == 0 -> null // "Select location..."
                        position < adapter.count - 1 -> adapter.getItem(position).toString() // Campus locations
                        else -> locationEditText.text.toString().takeIf { it.isNotBlank() } // "Other" with custom input
                    }
                    updateLocationFilterButtonText()
                    notifyFiltersChanged()
                }
                .setNegativeButton("Clear") { _, _ ->
                    selectedLocation = null
                    updateLocationFilterButtonText()
                    notifyFiltersChanged()
                }
                .setNeutralButton("Cancel", null)
                .show()
        }
    }

    private fun updateDateFilterButtonText() {
        val dateFormat = SimpleDateFormat("MMM dd", Locale.getDefault())

        dateFilterButton.text = when {
            startDate != null && endDate != null -> 
                "${dateFormat.format(startDate!!)} - ${dateFormat.format(endDate!!)}"
            startDate != null -> 
                "From ${dateFormat.format(startDate!!)}"
            endDate != null -> 
                "Until ${dateFormat.format(endDate!!)}"
            else -> 
                "Date Filter"
        }
    }

    private fun updateLocationFilterButtonText() {
        locationFilterButton.text = selectedLocation ?: "Location"
    }

    private fun notifyFiltersChanged() {
        onSearchFiltersChanged(searchQuery, selectedCategories, startDate, endDate, selectedLocation)
    }

    // Helper methods for converting between Date and Timestamp
    fun getStartTimestamp(): Timestamp? {
        return startDate?.let { Timestamp(it) }
    }

    fun getEndTimestamp(): Timestamp? {
        // If end date is specified, add 23:59:59 to include the whole day
        return endDate?.let {
            val calendar = Calendar.getInstance()
            calendar.time = it
            calendar.set(Calendar.HOUR_OF_DAY, 23)
            calendar.set(Calendar.MINUTE, 59)
            calendar.set(Calendar.SECOND, 59)
            Timestamp(calendar.time)
        }
    }

    fun clearAllFilters() {
        searchEditText.setText("")
        searchQuery = ""

        // Clear category selections
        for (i in 0 until categoryChipGroup.childCount) {
            val chip = categoryChipGroup.getChildAt(i) as Chip
            chip.isChecked = false
        }
        selectedCategories.clear()

        // Clear date filter
        startDate = null
        endDate = null
        dateFilterButton.text = "Date Filter"

        // Clear location filter
        selectedLocation = null
        locationFilterButton.text = "Location"

        // Notify that filters changed
        notifyFiltersChanged()
    }
}
